import React, { useState } from 'react';
import './sellerlogin.css';
import { Link, useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo/logo.png';
import logostruck from '../../assets/images/logo/logostruck.jpg';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerLogin = () => {
  const { handleSellerLogin } = useAppContext();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const validate = () => {
    const newErrors = {};
    if (!formData.email.trim()) newErrors.email = 'Vui l√≤ng nh·∫≠p email';
    if (!formData.password.trim()) newErrors.password = 'Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      const res = await axios.post('http://localhost:4005/api/auth/login', formData);
      const user = res.data.user;
      const token = res.data.token;

      if (!user || !token) {
        alert('ƒêƒÉng nh·∫≠p th·∫•t b·∫°i: Kh√¥ng nh·∫≠n ƒë∆∞·ª£c th√¥ng tin ng∆∞·ªùi d√πng');
        return;
      }

      if (user.role === 'seller') {
        handleSellerLogin(user, token);
        setTimeout(() => navigate('/seller'), 100);
      } else {
        alert('T√†i kho·∫£n c·ªßa b·∫°n ch∆∞a ƒëƒÉng k√Ω quy·ªÅn ng∆∞·ªùi b√°n.');
        navigate('/');
      }
    } catch (err) {
