import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
    const { user } = useAppContext();
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [form, setForm] = useState({
        name: '',
        phone: '',
        province: '',
        address: '',
        paymentMethod: 'cod'
    });

    useEffect(() => {
        const fetchCart = async () => {
            try {
                const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
                setCartItems(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy giỏ hàng:', error);
            } finally {
                setLoading(false);
            }
        };
        if (user?.id) fetchCart();
    }, [user]);

    const handleInputChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handlePlaceOrder = async () => {
        try {
            // 1. Tạo Payment
            const paymentRes = await axios.post('http://localhost:3005/api/payment', {
                paymentMethod: form.paymentMethod,
                paymentStatus: 'Pending'
            });

            // 2. Lấy Transport mặc định
            const transportRes = await axios.get('http://localhost:3004/api/transport/available');
            const selectedTransport = transportRes.data[0];

            // 3. Tạo Order
            const total = cartItems.reduce((t, item) => t + item.quantity * item.productId.price, 0);
            const orderRes = await axios.post('http://localhost:3006/api/order', {
                totalOrder: total,
                shippingInfo: {
                    name: form.name,
                    phone: form.phone,
                    province: form.province,
                    address: form.address
                },
                customerId: user.id,
                paymentId: paymentRes.data._id,
                transportId: selectedTransport._id,
                userId: user.id
            });

            // 4. Tạo OrderDetails
            const orderId = orderRes.data._id;
            for (let item of cartItems) {
                await axios.post('http://localhost:3006/api/order-details', {
                    orderId,
                    productId: item.productId._id,
                    quantity: item.quantity,
                    totalPrice: item.quantity * item.productId.price
                });
            }

            alert('Đặt hàng thành công!');
        } catch (error) {
            console.error('Đặt hàng thất bại:', error);
            alert('Đặt hàng thất bại!');
        }
    };

    return (
        <div className="payment-container">
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form">
                    <input type="text" name="name" placeholder="Họ và tên" required onChange={handleInputChange} />
                    <input type="number" name="phone" placeholder="Phone" required onChange={handleInputChange} />
                    <input type="text" name="province" placeholder="Tỉnh / Thành phố" required onChange={handleInputChange} />
                    <input type="text" name="address" placeholder="Địa chỉ chi tiết" required onChange={handleInputChange} />
                </form>
            </div>

            <div className="payment-method-section">
                <h3>Phương thức thanh toán</h3>
                <label>
                    <input type="radio" name="paymentMethod" value="cod" checked readOnly /> Thanh toán khi nhận hàng (COD)
                </label>
            </div>

            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {loading ? <p>Đang tải...</p> : cartItems.length === 0 ? <p>Giỏ hàng trống</p> : (
                    cartItems.map((item) => (
                        <div className="summary-item" key={item._id}>
                            <img src={item.productId.image[0]} alt={item.productId.name} />
                            <div>
                                <p>{item.productId.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: {item.productId.price.toLocaleString()} đ</p>
                            </div>
                        </div>
                    ))
                )}
            </div>

            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>{cartItems.reduce((total, item) => total + item.quantity * item.productId.price, 0).toLocaleString()} đ</p>
                <button className="btn-confirm" onClick={handlePlaceOrder}>Đặt hàng</button>
            </div>
        </div>
    );
};
