import React, { useState } from 'react';
import './sellerform.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerForm = () => {
    const { user } = useAppContext(); // Lấy userId
    const [images, setImages] = useState([]);
    const [storeName, setStoreName] = useState('');
    const [storeAddress, setStoreAddress] = useState('');
    const [phone, setPhone] = useState('');
    const [message, setMessage] = useState(''); // Trạng thái hiển thị thông báo

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('storeName', storeName);
        formData.append('storeAddress', storeAddress);
        formData.append('phone', phone);
        formData.append('userId', user._id); // hoặc userId cứng nếu chưa dùng auth

        images.forEach((image) => {
            if (image) formData.append('image', image); // Phải trùng tên với `upload.array('image')`
        });

        try {
            const res = await axios.post('http://localhost:3006/api/seller/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setMessage('Tạo Seller thành công!'); // Hiển thị thông báo thành công
            console.log(res.data);
        } catch (err) {
            console.error('Lỗi khi gửi dữ liệu:', err.response?.data || err.message);
            setMessage('Gửi thất bại!'); // Hiển thị thông báo lỗi
        }
    };

    return (
        <div className='form__seller-info'>
            <form className="form-container-seller" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Tên Shop</label>
                    <input type="text" className="form-input" value={storeName} onChange={(e) => setStoreName(e.target.value)} required />
                </div>

                <div className="form-group">
                    <label className="form-label">Địa chỉ</label>
                    <input type="text" className="form-input" value={storeAddress} onChange={(e) => setStoreAddress(e.target.value)} required />
                </div>

                <div className="form-group small">
                    <label className="form-label">Điện thoại</label>
                    <input type="text" className="form-input" value={phone} onChange={(e) => setPhone(e.target.value)} required />
                </div>

                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {[...Array(1)].map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <div className='btn__sellerInfo-submit'>
                    <button type="submit" className="submit-btn-save">Lưu</button>
                </div>

                {/* Hiển thị thông báo sau khi gửi dữ liệu */}
                {message && <div className="message">{message}</div>}
            </form>
        </div>
    );
};
