import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './myorder.css';

export const MyOrder = () => {
  const { user } = useAppContext();
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get(`http://localhost:4000/api/order/user/${user.id}`);
        setOrders(res.data || []);
      } catch (error) {
        console.error('Lỗi khi lấy đơn hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user?.id) {
      fetchOrders();
    }
  }, [user]);

  const handleCancelOrder = async (orderId) => {
    try {
      await axios.delete(`http://localhost:4000/api/order/cancel/${orderId}`);
      setOrders(orders.filter(order => order._id !== orderId)); // Loại bỏ đơn hàng đã hủy khỏi danh sách
      alert('Đơn hàng đã được hủy!');
    } catch (error) {
      console.error('Lỗi khi hủy đơn hàng:', error);
      alert('Không thể hủy đơn hàng, vui lòng thử lại!');
    }
  };

  return (
    <div className="my-order">
      <h2>Danh sách đơn hàng của tôi</h2>
      {loading ? (
        <p>Đang tải...</p>
      ) : orders.length === 0 ? (
        <p>Bạn chưa có đơn hàng nào.</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order._id} className="order-item">
              <div className="order-header">
                <span className="order-status">{order.status}</span>
                <span className="order-date">{new Date(order.createdAt).toLocaleDateString()}</span>
              </div>
              <div className="order-products">
                {order.orderDetails.map((detail, index) => (
                  <div key={index} className="product">
                    <p>{detail.productId.name} x{detail.quantity}</p>
                    <p>{detail.totalPrice.toLocaleString()} VND</p>
                  </div>
                ))}
              </div>
              <div className="order-footer">
                <span className="order-total">
                  Tổng cộng: {order.totalOrder.toLocaleString()} VND
                </span>
                {order.status !== 'Đã giao' && (
                  <button className="btn-cancel" onClick={() => handleCancelOrder(order._id)}>Hủy đơn hàng</button>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};
