import React, { useState } from 'react';
import './productdetails.css';
import { useParams , Link} from 'react-router-dom';
import { Button, Rate, Row, Col } from 'antd';
import { toSlug } from '../../utils/toSlug';
export const ProductDetails = () => {
  const product = {
    name: "Casual Shoes",
    category: "Sports",
    price: 100,
    offerPrice: 80,
    rating: 4,
    images: [
      "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/card/productImage.png",
      "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/card/productImage2.png",
      "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/card/productImage3.png",
      "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/card/productImage4.png"
    ],
    description: [
      "High-quality material",
      "Comfortable for everyday use",
      "Available in different sizes"
    ]
  };

  const [thumbnail, setThumbnail] = useState(product.images[0]);

  return (
    <div className="product-details">
      <p className="breadcrumb">
        <Link to={"/"}>Trang chủ</Link> / 
        <Link to={`/product/${toSlug(product.category)}`}>Sản phẩm</Link> / 
        <span>{product.category}</span> / 
        <span className="highlight">{product.name}</span>
      </p>

      <Row gutter={32} className="product-content">
        <Col xs={24} md={10}>
          <div className="product-images">
            <div className="product-thumbnails">
              {product.images.map((img, idx) => (
                <img
                  key={idx}
                  src={img}
                  alt={`thumbnail-${idx}`}
                  className={`thumbnail ${thumbnail === img ? 'active' : ''}`}
                  onClick={() => setThumbnail(img)}
                />
              ))}
            </div>
            <div className="main-image">
              <img src={thumbnail} alt="selected" />
            </div>
          </div>
        </Col>

        <Col xs={24} md={14}>
          <div className="product-info">
            <h1>{product.name}</h1>
            <div className="rating-section">
              <Rate disabled defaultValue={product.rating} />
              <span className="rating-number">({product.rating})</span>
            </div>

            <div className="price-section">
              <p className="price-original">MRP: ${product.price}</p>
              <p className="price-offer">MRP: ${product.offerPrice}</p>
              <span className="tax-note">(inclusive of all taxes)</span>
            </div>

            <p className="desc-title">About Product</p>
            <ul className="desc-list">
              {product.description.map((item, idx) => (
                <li key={idx}>{item}</li>
              ))}
            </ul>

            <div className="action-buttons">
              <Button block>Add to Cart</Button>
              <Button type="primary" block style={{ backgroundColor: '#615fff' }}>Buy now</Button>
            </div>
          </div>
        </Col>
      </Row>
    </div>
  );
};
