import React, { useState, useEffect } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { useNavigate } from 'react-router-dom';

export const AddProducts = () => {
    const { categories, user, token } = useAppContext(); // Destructure context values
    const navigate = useNavigate();

    const [productData, setProductData] = useState({
        name: '',
        description: '',
        category: '',
        status: 'in_stock',
        price: 0,
        quantity: 0
    });

    const [images, setImages] = useState([]);
    const [sellerData, setSellerData] = useState(null);

    // Fetch seller data based on the user when component mounts
    useEffect(() => {
        if (user && user._id && token) {
            axios.get(`http://localhost:3006/api/seller/by-user/${user._id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            .then(response => {
                setSellerData(response.data);
            })
            .catch(error => {
                console.error('Lỗi khi lấy thông tin người bán:', error);
                alert('Không thể lấy thông tin người bán.');
            });
        }
    }, [user, token]);

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setProductData(prev => ({ ...prev, [id]: value }));
    };

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Kiểm tra người dùng và seller
        if (!user || !user._id || !sellerData || !sellerData._id || !token) {
            alert('Vui lòng đăng nhập bằng tài khoản người bán.');
            return;
        }

        if (!productData.category) {
            alert('Vui lòng chọn danh mục.');
            return;
        }

        if (images.length === 0 || images.every(img => !img)) {
            alert('Vui lòng chọn ít nhất một hình ảnh.');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('name', productData.name);
            formData.append('description', productData.description);
            formData.append('categoryId', productData.category);
            formData.append('status', productData.status);
            formData.append('price', productData.price);
            formData.append('quantity', productData.quantity);
            formData.append('userId', user._id);
            formData.append('sellerId', sellerData._id);

            images.forEach((image) => {
                if (image) formData.append('image', image);
            });

            const res = await axios.post('http://localhost:4003/api/product/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${token}`
                }
            });

            alert('Sản phẩm đã được thêm thành công!');
            navigate('/seller/products');
        } catch (error) {
            console.error('Lỗi khi thêm sản phẩm:', error);
            const message = error?.response?.data?.message || 'Thêm sản phẩm thất bại.';
            alert(message);
        }
    };

    return (
        <div className="product-form">
            <form className="form-container" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Hình ảnh sản phẩm</label>
                    <div className="image-upload-group">
                        {Array(6).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="name" className="form-label">Tên sản phẩm</label>
                    <input
                        id="name"
                        type="text"
                        className="form-input"
                        placeholder="Nhập vào đây"
                        required
                        value={productData.name}
                        onChange={handleInputChange}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="description" className="form-label">Mô tả sản phẩm</label>
                    <textarea
                        id="description"
                        className="form-input"
                        rows="4"
                        placeholder="Nhập vào đây"
                        value={productData.description}
                        onChange={handleInputChange}
                    ></textarea>
                </div>

                <div className="form-group">
                    <label htmlFor="category" className="form-label">Danh mục</label>
                    <select
                        id="category"
                        className="form-input"
                        required
                        value={productData.category}
                        onChange={handleInputChange}
                    >
                        <option value="">Chọn danh mục</option>
                        {categories.map(cat => (
                            <option key={cat._id} value={cat._id}>{cat.name}</option>
                        ))}
                    </select>

                    <label htmlFor="status" className="form-label">Trạng thái</label>
                    <select
                        id="status"
                        className="form-input"
                        value={productData.status}
                        onChange={handleInputChange}
                    >
                        <option value="in_stock">Còn hàng</option>
                        <option value="out_of_stock">Hết hàng</option>
                    </select>
                </div>

                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="price" className="form-label">Giá sản phẩm</label>
                        <input
                            id="price"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            required
                            value={productData.price}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group small">
                        <label htmlFor="quantity" className="form-label">Số lượng</label>
                        <input
                            id="quantity"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            required
                            value={productData.quantity}
                            onChange={handleInputChange}
                        />
                    </div>
                </div>

                <button type="submit" className="submit-btn">THÊM SẢN PHẨM</button>
            </form>
        </div>
    );
};
