import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './cart.css';
import { useAppContext } from '../../context/AppContext';

export const Cart = () => {
  const { user } = useAppContext();
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  // Fetch cart items if user exists
  useEffect(() => {
    if (user && user.id) {
      axios.get(`http://localhost:3003/api/cart/user/${user.id}`)
        .then((res) => {
          if (res.data && Array.isArray(res.data)) {
            setCartItems(res.data);
            setSelectedItems(res.data.map(item => item._id)); // Select all by default
          }
        })
        .catch((err) => {
          console.error('Lỗi lấy giỏ hàng:', err);
        });
    }
  }, [user]);

  // Handle quantity change
  const handleQuantityChange = async (itemId, action) => {
    const updatedCart = cartItems.map(item => {
      if (item._id === itemId) {
        let newQuantity = action === 'increase'
          ? item.quantity + 1
          : item.quantity > 1 ? item.quantity - 1 : 1;

        // Update on server
        axios.put(`http://localhost:3003/api/cart/update/${itemId}`, {
          quantity: newQuantity
        });

        return { ...item, quantity: newQuantity };
      }
      return item;
    });

    setCartItems(updatedCart);
  };

  // Handle item deletion
  const handleDelete = async (itemId) => {
    try {
      await axios.delete(`http://localhost:3003/api/cart/delete/${itemId}`);
      setCartItems(cartItems.filter(item => item._id !== itemId));
      setSelectedItems(selectedItems.filter(id => id !== itemId));
    } catch (err) {
      console.error('Lỗi khi xóa sản phẩm:', err);
    }
  };

  // Toggle item selection
  const toggleSelectItem = (itemId) => {
    setSelectedItems(prev =>
      prev.includes(itemId) ? prev.filter(id => id !== itemId) : [...prev, itemId]
    );
  };

  // Select or deselect all items
  const handleSelectAll = () => {
    if (selectedItems.length === cartItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item._id));
    }
  };

  // Calculate total price of selected items
  const totalSelectedPrice = cartItems
    .filter(item => selectedItems.includes(item._id))
    .reduce((sum, item) => sum + item.product.price * item.quantity, 0);

  // Handle checkout process
  const handleBuy = () => {
    if (selectedItems.length === 0) {
      alert('Vui lòng chọn ít nhất một sản phẩm');
      return;
    }

    const selected = cartItems.filter(item => selectedItems.includes(item._id));
    navigate('/order', { state: { items: selected } });
  };

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={handleSelectAll}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item, index) => (
              item.product && (
                <div className="cart-item" key={index}>
                  <input
                    type="checkbox"
                    checked={selectedItems.includes(item._id)}
                    onChange={() => toggleSelectItem(item._id)}
                  />
                  {item.product.image && item.product.image[0] && (
                    <img src={item.product.image[0]} alt={item.product.name} />
                  )}
                  <div className="cart-item-info">
                    <h4>{item.product.name}</h4>
                    <p>Giá: đ{item.product.price.toLocaleString()}</p>

                    <div className="quantity-control">
                      <button onClick={() => handleQuantityChange(item._id, 'decrease')}>-</button>
                      <input type="text" value={item.quantity} readOnly />
                      <button onClick={() => handleQuantityChange(item._id, 'increase')}>+</button>
                    </div>

                    <p>Thành tiền: đ{(item.product.price * item.quantity).toLocaleString()}</p>
                    <button className="btn-delete" onClick={() => handleDelete(item._id)}>Xóa</button>
                  </div>
                </div>
              )
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={handleSelectAll}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>
                Tổng thanh toán: <span>đ{totalSelectedPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy" onClick={handleBuy}>Mua hàng</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
