import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAppContext } from '../../context/AppContext'; // Để lấy thông tin người dùng
import axios from 'axios';
import './cart.css';

export const Cart = () => {
  const navigate = useNavigate();
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCart = async () => {
      if (!user) {
        navigate('/login'); // Nếu chưa đăng nhập, chuyển đến trang đăng nhập
        return;
      }
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        setCartItems(res.data);
        setSelectedItems(res.data.map(item => item._id)); // Chọn mặc định tất cả sản phẩm trong giỏ
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [user, navigate]);

  const totalSelectedPrice = cartItems
    .filter(item => selectedItems.includes(item._id))
    .reduce((sum, item) => sum + item.productId.price * item.quantity, 0);

  const handleQuantityChange = async (cartId, action) => {
    const newQuantity = cartItems.find(item => item._id === cartId).quantity + (action === 'increase' ? 1 : -1);

    if (newQuantity <= 0) return;

    try {
      await axios.put(`http://localhost:3003/api/cart/update/${cartId}`, { quantity: newQuantity });
      setCartItems(cartItems.map(item => (item._id === cartId ? { ...item, quantity: newQuantity } : item)));
    } catch (error) {
      console.error('Lỗi khi thay đổi số lượng:', error);
    }
  };

  const handleDeleteItem = async (cartId) => {
    try {
      await axios.delete(`http://localhost:3003/api/cart/delete/${cartId}`);
      setCartItems(cartItems.filter(item => item._id !== cartId));
    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm khỏi giỏ:', error);
    }
  };

  if (loading) return <p>Đang tải giỏ hàng...</p>;

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={() => setSelectedItems(selectedItems.length === cartItems.length ? [] : cartItems.map(item => item._id))}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item) => (
              <div className="cart-item" key={item._id}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item._id)}
                  onChange={() => {
                    if (selectedItems.includes(item._id)) {
                      setSelectedItems(selectedItems.filter(id => id !== item._id));
                    } else {
                      setSelectedItems([...selectedItems, item._id]);
                    }
                  }}
                />
                <img src={item.productId.image[0]} alt={item.productId.name} />
                <div className="cart-item-info">
                  <h4>{item.productId.name}</h4>
                  <p>Giá: đ{item.productId.price.toLocaleString()}</p>

                  <div className="quantity-control">
                    <button onClick={() => handleQuantityChange(item._id, 'decrease')}>-</button>
                    <input type="text" value={item.quantity} readOnly />
                    <button onClick={() => handleQuantityChange(item._id, 'increase')}>+</button>
                  </div>

                  <p>Thành tiền: đ{(item.productId.price * item.quantity).toLocaleString()}</p>

                  <button className="btn-delete" onClick={() => handleDeleteItem(item._id)}>Xóa</button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={() => setSelectedItems(selectedItems.length === cartItems.length ? [] : cartItems.map(item => item._id))}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>
                Tổng thanh toán: <span>đ{totalSelectedPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy">Mua hàng</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
