import React, { useState, useEffect } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { useNavigate } from 'react-router-dom';

export const AddProducts = () => {
    const { categories, seller, user, token } = useAppContext();
    const navigate = useNavigate();

    const [productData, setProductData] = useState({
        name: '',
        description: '',
        category: '',
        status: 'in_stock',
        price: 0,
        quantity: 0
    });

    const [images, setImages] = useState([]);
    const [sellerData, setSellerData] = useState(null); // Lưu thông tin seller

    // Lấy thông tin người bán khi component được load
    useEffect(() => {
        if (user && user._id && token) {
            axios.get(`http://localhost:3006/api/seller/${user._id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            .then(response => {
                setSellerData(response.data); // Lưu seller data vào state
            })
            .catch(error => {
                console.error('Lỗi khi lấy thông tin người bán:', error);
                alert('Không thể lấy thông tin người bán.');
            });
        }
    }, [user, token]);

    const handleInputChange = (e) => {
        setProductData({ ...productData, [e.target.id]: e.target.value });
    };

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // ✅ Kiểm tra đăng nhập và thông tin người bán
        if (!user || !user._id || !sellerData || !sellerData._id || !token) {
            alert('Vui lòng đăng nhập bằng tài khoản người bán.');
            return;
        }

        const userId = user._id;
        const sellerId = sellerData._id;

        if (!productData.status) {
            alert('Trạng thái sản phẩm là bắt buộc.');
            return;
        }

        if (images.length === 0) {
            alert('Vui lòng chọn ít nhất một hình ảnh.');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('name', productData.name);
            formData.append('describe', productData.description);
            formData.append('categoryId', productData.category);
            formData.append('status', productData.status);
            formData.append('price', productData.price);
            formData.append('quantity', productData.quantity);
            formData.append('userId', userId);
            formData.append('sellerId', sellerId);

            // Gửi các ảnh
            images.forEach((image) => {
                if (image) formData.append('image', image);
            });

            const res = await axios.post('http://localhost:4003/api/product/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${token}`
                }
            });

            alert('Sản phẩm đã được thêm thành công!');
            navigate('/seller/products');
        } catch (error) {
            console.error('Lỗi khi thêm sản phẩm:', error);
            if (error.response) {
                alert(`Thêm sản phẩm thất bại. Lỗi: ${error.response.data.message || error.response.statusText}`);
            } else if (error.request) {
                alert('Lỗi kết nối đến server. Vui lòng thử lại sau.');
            } else {
                alert('Đã xảy ra lỗi. Vui lòng thử lại.');
            }
        }
    };

    return (
        <div className="product-form">
            <form className="form-container" onSubmit={handleSubmit}>
                {/* Form Fields */}
                {/* ... */}
                <button type="submit" className="submit-btn">ADD</button>
            </form>
        </div>
    );
};
