import React, { useState } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { useNavigate } from 'react-router-dom';

export const AddProducts = () => {
    const { categories, user } = useAppContext();
    const navigate = useNavigate();

    const [images, setImages] = useState(Array(6).fill(null));
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [category, setCategory] = useState('');
    const [status, setStatus] = useState('in_stock');
    const [price, setPrice] = useState(0);
    const [quantity, setQuantity] = useState(0);

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('name', name);
        formData.append('description', description);
        formData.append('category', category);
        formData.append('status', status);
        formData.append('price', price);
        formData.append('quantity', quantity);
        formData.append('sellerId', localStorage.getItem('sellerId'));
        formData.append('userId', user?._id);// üëà l·∫•y sellerId t·ª´ localStorage

        images.forEach((img) => {
            if (img) {
                formData.append('images', img);
            }
        });

        try {
            const res = await axios.post('http://localhost:4003/api/product/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            alert('Th√™m s·∫£n ph·∫©m th√†nh c√¥ng!');
            navigate('/seller/products');
        } catch (error) {
            console.error('L·ªói khi th√™m s·∫£n ph·∫©m:', error.message);
            alert('Th√™m s·∫£n ph·∫©m th·∫•t b·∫°i.');
        }
    };

    return (
        <div className="product-form">
            <form className="form-container" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">H√¨nh ·∫£nh s·∫£n ph·∫©m</label>
                    <div className="image-upload-group">
                        {Array(6).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={
                                        images[index]
                                            ? URL.createObjectURL(images[index])
                                            : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"
                                    }
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="name" className="form-label">T√™n s·∫£n ph·∫©m</label>
                    <input
                        id="name"
                        type="text"
                        className="form-input"
                        placeholder="Nh·∫≠p v√†o ƒë√¢y"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="description" className="form-label">M√¥ t·∫£ s·∫£n ph·∫©m</label>
                    <textarea
                        id="description"
                        className="form-input"
                        rows="4"
                        placeholder="Nh·∫≠p v√†o ƒë√¢y"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    ></textarea>
                </div>

                <div className="form-group">
                    <label htmlFor="category" className="form-label">Danh m·ª•c</label>
                    <select
                        id="category"
                        className="form-input"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        required
                    >
                        <option value="">Ch·ªçn danh m·ª•c</option>
                        {categories.map(cat => (
                            <option key={cat._id} value={cat._id}>{cat.name}</option>
                        ))}
                    </select>

                    <label htmlFor="status" className="form-label">Tr·∫°ng th√°i</label>
                    <select
                        id="status"
                        className="form-input"
                        value={status}
                        onChange={(e) => setStatus(e.target.value)}
                        required
                    >
                        <option value="in_stock">C√≤n h√†ng</option>
                        <option value="out_of_stock">H·∫øt h√†ng</option>
                    </select>
                </div>

                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="price" className="form-label">Gi√° s·∫£n ph·∫©m</label>
                        <input
                            id="price"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            value={price}
                            onChange={(e) => setPrice(Number(e.target.value))}
                            required
                        />
                    </div>
                    <div className="form-group small">
                        <label htmlFor="quantity" className="form-label">S·ªë l∆∞·ª£ng</label>
                        <input
                            id="quantity"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            value={quantity}
                            onChange={(e) => setQuantity(Number(e.target.value))}
                            required
                        />
                    </div>
                </div>

                <button type="submit" className="submit-btn">
                    TH√äM S·∫¢N PH·∫®M
                </button>
            </form>
        </div>
    );
};
