import React from 'react';
import { useParams } from 'react-router-dom';
import { useAppContext } from '../../context/AppContext';
import categoryData from '../../assets/data/category'; // nhập danh sách category

export const ProductCategory = () => {
    const { category } = useParams();
    const { product } = useAppContext();

    const toSlug = (str) =>
        str.normalize("NFD")
            .replace(/[\u0300-\u036f]/g, "")
            .replace(/đ/g, "d")
            .replace(/Đ/g, "d")
            .replace(/[^a-zA-Z0-9\s]/g, "")
            .trim()
            .toLowerCase()
            .replace(/\s+/g, "-");

    const searchCategory = categoryData.find(item => toSlug(item.name) === category);
    const filteredProduct = product.filter((item) => toSlug(item.category) === category);


    return (
        <div>
            {searchCategory && (
                <>
                    <h2>{searchCategory.name.toUpperCase()}</h2> {/* Tiêu đề danh mục */}
                    <div className="filtered-products">
                        {filteredProduct.length > 0 ? (
                            filteredProduct.map((item) => (
                                <div key={item._id} className="product-card">
                                    <img src={item.img} alt={item.name} />
                                    <p>{item.name}</p>
                                    <p>{item.price}₫</p>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong danh mục này.</p>
                        )}
                    </div>
                </>
            )}

        </div>
    );
};
