import React, { useState } from 'react';

const TopSearchItem = ({ item }) => {
  const [isTop, setIsTop] = useState(item.isTop);

  return (
    <div className="topsearch-item">
      <div className="topsearch__img">
        {isTop && <div className="top-ribbon">TOP</div>}
        <img src={item.img} alt={item.name} />
        <div className="topsearch__sales">Bán {item.sales} / tháng</div>
      </div>
      <p className="product-name">{item.name}</p>

      {/* Button để toggle trạng thái TOP */}
      <button onClick={() => setIsTop(!isTop)} style={{ marginTop: '8px' }}>
        {isTop ? 'Ẩn TOP' : 'Hiện TOP'}
      </button>
    </div>
  );
};
