import React, { createContext, useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const AppContext = createContext();

export const AppContextProvider = ({ children }) => {
  const [customer, setCustomer] = useState(null);
  const [seller, setSeller] = useState(null);
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState(() => {
    const storedCustomer = localStorage.getItem('customer');
    const userId = storedCustomer ? JSON.parse(storedCustomer)?._id || JSON.parse(storedCustomer)?.id : null;
    const cartKey = userId ? `cartItems_user_${userId}` : 'cartItems_guest';
    const storedCart = localStorage.getItem(cartKey);
    const initialCart = storedCart ? JSON.parse(storedCart) : [];
    console.log('Initial cartItems from Local Storage:', initialCart);
    return initialCart;
  });
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  const getCartKey = (userId) => {
    return userId ? `cartItems_user_${userId}` : 'cartItems_guest';
  };

  const getProfileKey = (userId) => {
    return userId ? `customerProfile_user_${userId}` : 'customerProfile_guest';
  };

  const [customerProfile, setCustomerProfile] = useState(() => {
    const storedCustomer = localStorage.getItem('customer');
    const userId = storedCustomer ? JSON.parse(storedCustomer)?._id || JSON.parse(storedCustomer)?.id : null;
    const profileKey = userId ? `customerProfile_user_${userId}` : 'customerProfile_guest';
    const storedProfile = localStorage.getItem(profileKey);
    return storedProfile ? JSON.parse(storedProfile) : { phone: '', birthday: '', gender: '', city: '', describe: '' };
  });


  const handleLogout = () => {
    const storedCustomer = localStorage.getItem('customer');
    const userId = storedCustomer ? JSON.parse(storedCustomer)?._id || JSON.parse(storedCustomer)?.id : null;

    if (userId) {
      localStorage.removeItem(getCartKey(userId)); // Xóa giỏ hàng của người dùng
      localStorage.removeItem(getProfileKey(userId)); // Xóa profile của người dùng
    }
    localStorage.removeItem('token');
    localStorage.removeItem('customer');
    setCustomer(null);
    setSeller(null);
    setCartItems([]);
    setCustomerProfile({ phone: '', birthday: '', gender: '', city: '', describe: '' });
    navigate('/login');
  };

  const fetchCartItems = async (customerId) => {
    try {
      const res = await axios.get(`http://localhost:3003/api/cart/customer/${customerId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      console.log('Cart data from server:', res.data);
      if (Array.isArray(res.data)) {
        const formattedCart = res.data.map(item => ({
          _id: item._id,
          productId: item.productId._id,
          quantity: item.quantity,
          productName: item.productId.name,
          productImage: item.productId.image?.[0],
          productPrice: item.productId.price
        }));
        setCartItems(formattedCart);
        localStorage.setItem(getCartKey(customerId), JSON.stringify(formattedCart));
      } else {
        const storedCart = localStorage.getItem(getCartKey(customerId));
        if (storedCart) {
          setCartItems(JSON.parse(storedCart));
        } else {
          setCartItems([]);
        }
      }
    } catch (error) {
      console.error("Lỗi khi tải giỏ hàng:", error);
      const storedCart = localStorage.getItem(getCartKey(customerId));
      if (storedCart) {
        setCartItems(JSON.parse(storedCart));
      } else {
        setCartItems([]);
      }
    }
  };

  const fetchCategories = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/category/all');
      setCategories(res.data || []);
    } catch (error) {
      console.error('Lỗi khi lấy danh mục:', error.message);
    }
  };

  const addToCart = async (product, quantity) => {
    try {
      const storedCustomer = JSON.parse(localStorage.getItem('customer'));
      const customerId = storedCustomer?._id || storedCustomer?.id;
      const cartKey = getCartKey(customerId);

      setCartItems(prev => {
        const existingItem = prev.find(item => item.productId === product._id);
        let updatedCart;
        if (existingItem) {
          updatedCart = prev.map(item =>
            item.productId === product._id
              ? { ...item, quantity: item.quantity + quantity }
              : item
          );
        } else {
          updatedCart = [
            ...prev,
            {
              productId: product._id,
              quantity,
              productName: product.name,
              productImage: product.image?.[0],
              productPrice: product.price
            }
          ];
        }
        localStorage.setItem(cartKey, JSON.stringify(updatedCart));
        console.log('AppContext - addToCart - updatedCart:', updatedCart);
        return updatedCart;
      });

      if (customerId) {
        await axios.post('http://localhost:3003/api/cart/create', {
          productId: product._id,
          quantity,
          customerId
        }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }

    } catch (error) {
      console.log(error);
    }
  };

  const fetchProduct = async () => {
    try {
      const res = await axios.get('http://localhost:4003/api/product/all');
      setProducts(res.data.products || []);
    } catch (error) {
      console.error('Lỗi khi lấy danh sách sản phẩm:', error.message);
    }
  };

  // Hàm xử lý đăng nhập
  const handleLogin = (customerData) => {
    const userId = customerData?._id || customerData?.id;
    const profileKey = getProfileKey(userId);
    const cartKey = getCartKey(userId);
    const storedProfile = localStorage.getItem(profileKey);

    setCustomer(customerData);
    if (customerData.role === 'seller') {
      setSeller(customerData);
    }
    if (storedProfile) {
      setCustomerProfile(JSON.parse(storedProfile));
    } else {
      setCustomerProfile({ phone: '', birthday: '', gender: '', city: '', describe: '' });
    }
    localStorage.setItem('customer', JSON.stringify(customerData));
    fetchCartItems(userId);
  };

  useEffect(() => {
    fetchProduct();
    fetchCategories();

    const storedCustomer = localStorage.getItem('customer');
    if (storedCustomer) {
      const customerData = JSON.parse(storedCustomer);
      const userId = customerData?._id || customerData?.id;
      const profileKey = getProfileKey(userId);
      const storedProfile = localStorage.getItem(profileKey);

      setCustomer(customerData);
      if (customerData.role === 'seller') {
        setSeller(customerData);
      }
      if (storedProfile) {
        setCustomerProfile(JSON.parse(storedProfile));
      }
      fetchCartItems(userId);
    }
  }, []);

  useEffect(() => {
    if (customer) {
      const userId = customer?._id || customer?.id;
      const profileKey = getProfileKey(userId);
      localStorage.setItem(profileKey, JSON.stringify(customerProfile));
    }
  }, [customerProfile, customer]);

  useEffect(() => {
    console.log('AppContext - cartItems changed:', cartItems);
    if (customer) {
      const userId = customer?._id || customer?.id;
      const cartKey = getCartKey(userId);
      localStorage.setItem(cartKey, JSON.stringify(cartItems));
      console.log('AppContext - cartItems saved to Local Storage (logged in):', cartItems);
    } else {
      localStorage.setItem('cartItems_guest', JSON.stringify(cartItems));
      console.log('AppContext - cartItems saved to Local Storage (guest):', cartItems);
    }
  }, [cartItems, customer]);

  const value = {
    navigate,
    products,
    cartItems,
    setCartItems,
    addToCart,
    customer,
    seller,
    setCustomer,
    setSeller,
    categories,
    handleLogout,
    customerProfile,
    setCustomerProfile,
    handleLogin,
    fetchCartItems // Thêm fetchCartItems vào value
  };

  return (
    <AppContext.Provider value={value}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => useContext(AppContext);
