import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import { Link, useNavigate } from 'react-router-dom';
import './cart.css';
import axios from 'axios';

export const Cart = () => {
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login'); // Redirect to login if the user is not logged in
      return;
    }

    const fetchCartItems = async () => {
      try {
        const response = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        setCartItems(response.data);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCartItems();
  }, [user, navigate]);

  useEffect(() => {
    const calculateTotalPrice = () => {
      const total = cartItems.reduce(
        (acc, item) => acc + item.productId.price * item.quantity,
        0
      );
      setTotalPrice(total);
    };
    calculateTotalPrice();
  }, [cartItems]);

  const handleRemoveItem = async (itemId) => {
    try {
      const response = await axios.delete(`http://localhost:3003/api/cart/${itemId}`);
      if (response.data.success) {
        setCartItems(cartItems.filter((item) => item._id !== itemId));
      }
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const handleQuantityChange = async (itemId, action) => {
    const updatedCartItems = cartItems.map((item) => {
      if (item._id === itemId) {
        if (action === 'increase') {
          item.quantity += 1;
        } else if (action === 'decrease' && item.quantity > 1) {
          item.quantity -= 1;
        }
      }
      return item;
    });
    setCartItems(updatedCartItems);

    // Update the backend with the new quantity
    try {
      await axios.put(`http://localhost:3003/api/cart/${itemId}`, {
        quantity: updatedCartItems.find(item => item._id === itemId).quantity,
      });
    } catch (error) {
      console.error('Error updating quantity:', error);
    }
  };

  if (loading) return <p>Đang tải...</p>;
  if (cartItems.length === 0) return <p>Giỏ hàng của bạn đang trống.</p>;

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      <div className="cart__items">
        {cartItems.map((item) => (
          <div className="cart__item" key={item._id}>
            <div className="cart__item-img">
              <img src={item.productId.image[0]} alt={item.productId.name} />
            </div>
            <div className="cart__item-info">
              <Link to={`/product/${item.productId._id}`}>
                <h4>{item.productId.name}</h4>
              </Link>
              <p>Giá: đ{item.productId.price}</p>
              <div className="cart__item-quantity">
                <button onClick={() => handleQuantityChange(item._id, 'decrease')}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item._id, 'increase')}>+</button>
              </div>
            </div>
            <div className="cart__item-remove">
              <button onClick={() => handleRemoveItem(item._id)}>Xoá</button>
            </div>
          </div>
        ))}
      </div>

      <div className="cart__summary">
        <p>Tổng cộng: đ{totalPrice}</p>
        <button className="cart__checkout-btn">Thanh toán</button>
      </div>
    </div>
  );
};
