import React, { useState, useEffect } from 'react';
import './customerprofile.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const CustomerProfile = () => {
    const [formData, setFormData, isAppInitialized] = useState({
        fullName: '',
        phone: '',
        address: '',
        birthday: '',
        gender: '',
    });
    const { user } = useAppContext();
    const [isUserLoaded, setIsUserLoaded] = useState(false);

    useEffect(() => {
        if (user && user.id) { // đổi từ _id -> id
            setIsUserLoaded(true);
        } else {
            setIsUserLoaded(false);
            console.log('User data not yet loaded in CustomerProfile.');
        }
    }, [user]);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (user && user.id) {
            try {
                const response = await axios.post('http://localhost:3002/api/customer/create', {
                    ...formData,
                    userId: user.id, // đổi từ _id sang id
                });
                console.log('Customer created:', response.data);
            } catch (error) {
                console.error('Error creating customer:', error.message);
            }
        } else {
            console.error('User không tồn tại hoặc chưa đăng nhập.');
        }
    };

    if (!isAppInitialized) {
        return <div>Đang tải thông tin người dùng...</div>;
    }

    if (!user || !user._id) {
        return <div>Không tìm thấy người dùng. Vui lòng đăng nhập.</div>;
    }


    return (
        <div className="customer-profile">
            <h2>Thông Tin Khách Hàng</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Họ và tên</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Điện Thoại</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Địa chỉ</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>

                <div className="form-group">
                    <label>Sinh Nhật</label>
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                        className="input-date"
                    />
                </div>

                <div className="form-group">
                    <label>Giới tính</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-gender"
                    >
                        <option value="">Chọn giới tính</option>
                        <option value="male">Nam</option>
                        <option value="female">Nữ</option>
                        <option value="other">Khác</option>
                    </select>
                </div>
                <button type="submit" className='updateProfile'>
                    Lưu
                </button>
            </form>
        </div>
    );
};
