import React from 'react';
import Slider from 'react-slick';
import slider1 from '../../assets/images/slider1.jpg';
import slider2 from '../../assets/images/slider2.jpg';
import slider3 from '../../assets/images/slider3.jpg';
import slider4 from '../../assets/images/slider4.jpg';
import slider5 from '../../assets/images/slider5.jpg';
import slider7 from '../../assets/images/slider7.jpg';
import './slide.css'
import { Container, Row, Button, FormGroup } from "reactstrap";
export const Slide = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                }
            }
        ]
    };

    // Danh sách ảnh cho slide bên trái
    const slideImages = [slider1, slider2, slider3, slider4];

    return (
        <Container className=''>
            <div className='slide__left'>
                <Slider {...settings}>
                    {slideImages.map((img, index) => (
                        <div key={index}>
                            <img src={img} alt={`Slide${index}`}/>
                        </div>
                    ))}
                </Slider>
            </div>
            <div className='slide__right d-flex'>
                <img src={slider5} alt="Slide 5" />
                <img src={slider7} alt="Slide 7" />
            </div>
        </Container>
    );
};
