import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import { Link, useNavigate } from 'react-router-dom';
import './cart.css';
import axios from 'axios';

export const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const { user } = useAppContext();
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
      // Fetch cart items from the server
      axios.get(`http://localhost:3003/api/cart/user/${user.id}`)
        .then((response) => {
          setCartItems(response.data);
          calculateTotalPrice(response.data);
        })
        .catch((error) => {
          console.error('Error fetching cart:', error);
        });
    } else {
      navigate('/login');
    }
  }, [user, navigate]);

  const calculateTotalPrice = (items) => {
    const total = items.reduce((acc, item) => acc + item.quantity * item.productId.price, 0);
    setTotalPrice(total);
  };

  const handleRemoveItem = (id) => {
    axios.delete(`http://localhost:3003/api/cart/delete/${id}`)
      .then((response) => {
        const updatedCart = cartItems.filter(item => item._id !== id);
        setCartItems(updatedCart);
        calculateTotalPrice(updatedCart);
      })
      .catch((error) => {
        console.error('Error removing item from cart:', error);
      });
  };

  const handleUpdateQuantity = (id, quantity) => {
    if (quantity <= 0) return;

    axios.put(`http://localhost:3003/api/cart/update/${id}`, { quantity })
      .then((response) => {
        const updatedCart = cartItems.map(item => item._id === id ? { ...item, quantity } : item);
        setCartItems(updatedCart);
        calculateTotalPrice(updatedCart);
      })
      .catch((error) => {
        console.error('Error updating quantity:', error);
      });
  };

  const handleCheckout = () => {
    if (!cartItems.length) {
      alert('Giỏ hàng của bạn đang trống!');
      return;
    }
    navigate('/checkout');
  };

  if (!user) return <p>Vui lòng đăng nhập để xem giỏ hàng.</p>;

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      {cartItems.length === 0 ? (
        <p>Giỏ hàng của bạn hiện tại không có sản phẩm nào.</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <div key={item._id} className="cart-item">
                <img src={item.productId.image[0]} alt={item.productId.name} className="cart-item-img" />
                <div className="cart-item-info">
                  <h3>{item.productId.name}</h3>
                  <p>Giá: {item.productId.price} VND</p>
                  <div className="cart-item-quantity">
                    <button onClick={() => handleUpdateQuantity(item._id, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => handleUpdateQuantity(item._id, item.quantity + 1)}>+</button>
                  </div>
                  <p>Thành tiền: {item.quantity * item.productId.price} VND</p>
                  <button onClick={() => handleRemoveItem(item._id)} className="remove-btn">Xóa</button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-total">
            <p>Tổng cộng: {totalPrice} VND</p>
            <button className="checkout-btn" onClick={handleCheckout}>Tiến hành thanh toán</button>
          </div>
        </>
      )}
    </div>
  );
};
