import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useAppContext } from '../../context/AppContext';
import { useParams, Link } from 'react-router-dom';
import { Button, Col, Row } from 'antd';
import { toSlug } from '../../utils/toSlug';
export const ProductDetails = () => {
  const { id } = useParams();
  const { product, navigate } = useAppContext();
  const [thumbnail, setThumbnail] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const products = product.find((item) => item.id === Number(id));

  
  useEffect(()=>{
    if(product.length>0){
      let productsCopy=products.slice();
      productsCopy = productsCopy.filter((item) => products?.category === item.category);

      setRelatedProducts(productsCopy.slice(0,5))
    }
  }, [product])

  useEffect(()=>{
    setThumbnail(product?.image[0]?product.image[0]:null)
  }, [products])


  return (
    <>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link>{products?.name}</Link></li>
      </ul>
  
      {products ? (
        <div className="product-detail">
          <Row>
            <Col span={12}>
              <img src={thumbnail} alt={products.name} />
            </Col>
            <Col span={12}>
              <h2>{products.name}</h2>
              <p>{products.price}</p>
              <Button>Mua ngay</Button>
            </Col>
          </Row>
        </div>
      ) : (
        <p>Không tìm thấy sản phẩm</p>
      )}
    </>
  );
};
