import React, { useEffect, useState } from "react";
import "./product.css";
import { Button, Col, Row } from "antd";
import { useNavigate } from "react-router-dom";
import { toSlug } from "../../utils/toSlug";
import axios from "axios";

export const Product = () => {
  const [products, setProducts] = useState([]); // State để lưu danh sách sản phẩm
  const navigate = useNavigate();

  // Gọi API để lấy danh sách sản phẩm
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(
          "http://localhost:4003/api/product/all"
        ); // URL API
        setProducts(response.data); // Lưu dữ liệu vào state
      } catch (error) {
        console.error("Lỗi khi lấy danh sách sản phẩm:", error);
      }
    };

    fetchProducts();
  }, []); // Chỉ gọi API một lần khi component được mount

  return (
    <section className="product">
      <Row className="product__title">
        <h4>Gợi ý hôm nay</h4>
      </Row>
      <Row>
        <Col span={24}>
          <div className="product__list">
            {products.map((item) => (
              <div
                key={item.id}
                className="product__item"
                onClick={() =>
                  navigate(`/product/${toSlug(item.category)}/${item.id}`)
                }
              >
                <div>
                  <img
                    src={item.img[0]}
                    alt={item.name}
                    className="product__item-img"
                  />
                </div>
                <div className="product__item-info">
                  <p className="product__item-name">{item.name}</p>
                  <span className="product__item-price">
                    {item.original_price} VND
                  </span>
                </div>
              </div>
            ))}
          </div>
        </Col>
      </Row>
      <Button className="product__btn">Xem thêm</Button>
    </section>
  );
};
