import React from 'react'

export const Banner = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                }
            }
        ]
    };
  return (
    <section>
        <div>
            <img src="https://cf.shopee.vn/file/vn-11134258-7ra0g-m805ryoe5ls2ef"/>
            <img src="https://cf.shopee.vn/file/vn-50009109-727a24a85a60935da5ccb9008298f681"/>
        </div>
    </section>
  )
}
