import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
    const { cartItems, products, customerId, cartId } = useAppContext();
    const [paymentMethod, setPaymentMethod] = useState('cod');

    const selectedItems = cartItems.filter(item => item.selected);

    const getProductById = (id) => products.find((p) => p._id === id);

    const totalPrice = selectedItems.reduce((acc, item) => {
        const prod = getProductById(item.productId);
        if (!prod) return acc;
        const price = prod.discounted_price || prod.price || 0;
        return acc + price * item.quantity;
    }, 0);

    const handleOrderSubmit = async () => {
        try {
            // 1. Tạo Payment trước (nếu bạn dùng phương thức thanh toán)
            const paymentRes = await axios.post('http://localhost:3008/api/paymentMethod/create', {
                method: paymentMethod,
                status: 'pending'
            });
            const paymentId = paymentRes.data._id;

            // 2. Tạo Order
            const orderRes = await axios.post('http://localhost:3007/api/order/create', {
                totalOrder: totalPrice,
                discountId: [], // Chưa xử lý giảm giá
                cartId: cartId,
                customerId: customerId,
                paymentId: paymentId,
                status: 'pending'
            });

            const orderId = orderRes.data.order._id;

            // 3. Tạo từng OrderDetails
            await Promise.all(selectedItems.map(async (item) => {
                const prod = getProductById(item.productId);
                const price = prod.discounted_price || prod.price || 0;

                await axios.post('http://localhost:3007/api/orderDetails/create', {
                    orderId,
                    productId: item.productId,
                    paymentId,
                    quantity: item.quantity,
                    totalPrice: price * item.quantity
                });
            }));

            alert('Đặt hàng thành công!');
        } catch (error) {
            console.error('Lỗi khi đặt hàng:', error);
            alert('Đặt hàng thất bại!');
        }
    };

    return (
        <div className="payment-container">
            {/* Địa chỉ */}
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form">
                    <input type="text" placeholder="Họ và tên" required />
                    <input type="text" placeholder="Email" required />
                    <input type="text" placeholder="Phone" required />
                    <input type="text" placeholder="Tỉnh / Thành phố" required />
                    <input type="text" placeholder="Địa chỉ chi tiết" required />
                </form>
            </div>

            {/* Phương thức thanh toán */}
            <div className="payment-method-section">
                <h3>Phương thức thanh toán</h3>
                <div className="payment-options">
                    {['cod', 'bank', 'credit'].map(method => (
                        <label key={method}>
                            <input
                                type="radio"
                                name="paymentMethod"
                                value={method}
                                checked={paymentMethod === method}
                                onChange={() => setPaymentMethod(method)}
                            />
                            {method === 'cod' && 'Thanh toán khi nhận hàng (COD)'}
                            {method === 'bank' && 'Chuyển khoản ngân hàng'}
                            {method === 'credit' && 'Thẻ tín dụng / Ghi nợ'}
                        </label>
                    ))}
                </div>
            </div>

            {/* Danh sách sản phẩm */}
            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {selectedItems.map((item, index) => {
                    const prod = getProductById(item.productId);
                    if (!prod) return null;

                    const price = prod.discounted_price || prod.price || 0;
                    return (
                        <div className="summary-item" key={index}>
                            <img
                                src={prod.img?.[0] || prod.image?.[0] || 'fallback.jpg'}
                                alt={prod.name}
                            />
                            <div>
                                <p>{prod.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: đ{(price * item.quantity).toLocaleString('vi-VN')}</p>
                            </div>
                        </div>
                    );
                })}
            </div>

            {/* Tổng tiền và nút đặt hàng */}
            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
                <button className="btn-confirm" onClick={handleOrderSubmit}>
                    Đặt hàng
                </button>
            </div>
        </div>
    );
};
