import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './cart.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const Cart = () => {
  const navigate = useNavigate();
  const { user } = useAppContext();

  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    const fetchCart = async () => {
      if (!user || !user.id) return;
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        const items = res.data || [];
        setCartItems(items);
        setSelectedItems(items.map(item => item.productId));
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error.message);
      }
    };
    fetchCart();
  }, [user]);

  const handleSelectItem = (productId) => {
    setSelectedItems(prev =>
      prev.includes(productId)
        ? prev.filter(id => id !== productId)
        : [...prev, productId]
    );
  };

  const handleSelectAll = () => {
    if (selectedItems.length === cartItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item.productId));
    }
  };

  const handleQuantityChange = async (productId, type) => {
    const updatedItems = cartItems.map(item => {
      if (item.productId === productId) {
        const newQty = type === 'increase' ? item.quantity + 1 : item.quantity - 1;
        if (newQty >= 1) {
          updateCart(productId, newQty);
          return { ...item, quantity: newQty };
        }
      }
      return item;
    });
    setCartItems(updatedItems);
  };

  const updateCart = async (productId, quantity) => {
    try {
      await axios.put(`http://localhost:3003/api/cart/update`, {
        customerId: user.id,
        productId,
        quantity,
      });
    } catch (error) {
      console.error('Lỗi khi cập nhật số lượng:', error.message);
    }
  };

  const deleteItem = async (productId) => {
    try {
      await axios.delete(`http://localhost:3003/api/cart/delete`, {
        data: { customerId: user.id, productId },
      });
      const updated = cartItems.filter(item => item.productId !== productId);
      setCartItems(updated);
      setSelectedItems(selectedItems.filter(id => id !== productId));
    } catch (error) {
      console.error('Lỗi khi xoá sản phẩm:', error.message);
    }
  };

  const totalSelectedPrice = cartItems
    .filter(item => selectedItems.includes(item.productId))
    .reduce((sum, item) => sum + Number(item.productPrice) * item.quantity, 0);

  const handleCheckout = () => {
    if (selectedItems.length === 0) {
      alert('Vui lòng chọn ít nhất 1 sản phẩm để mua.');
      return;
    }

    const selected = cartItems.filter(item => selectedItems.includes(item.productId));
    localStorage.setItem('selectedCartItems', JSON.stringify(selected));
    navigate('/order');
  };

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={handleSelectAll}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={`${item.productId}-${index}`}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.productId)}
                  onChange={() => handleSelectItem(item.productId)}
                />
                <img src={item.productImage || '/no-image.png'} alt={item.productName} />
                <div className="cart-item-info">
                  <h4>{item.productName}</h4>
                  <p>Giá: đ{Number(item.productPrice).toLocaleString()}</p>

                  <div className="quantity-control">
                    <button onClick={() => handleQuantityChange(item.productId, 'decrease')}>-</button>
                    <input type="text" value={item.quantity} readOnly />
                    <button onClick={() => handleQuantityChange(item.productId, 'increase')}>+</button>
                  </div>

                  <p>Thành tiền: đ{(Number(item.productPrice) * item.quantity).toLocaleString()}</p>

                  <button className="btn-delete" onClick={() => deleteItem(item.productId)}>
                    Xóa
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={handleSelectAll}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>
                Tổng thanh toán: <span>đ{totalSelectedPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy" onClick={handleCheckout}>
                Mua hàng
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
