import React, { useState } from 'react';
import logo from '../../assets/images/logo/logo.png';
import { NavLink, Link } from 'react-router-dom'; // Không cần Form ở đây
import './register.css';

export const Register = () => {
  const [phoneNumber, setPhoneNumber] = useState('');

  const handlePhoneChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Xử lý đăng ký hoặc chuyển sang bước tiếp theo
    console.log('Đăng ký với số điện thoại:', phoneNumber);
  };

  return (
    <div className='register'>
      <header className='header__register'>
        <div className='header__register-box'>
          <div className='header__register-logo'>
            <img src={logo} alt="Logo" />
            <h3>Đăng ký</h3>
          </div>
          <a href="#">Bạn cần giúp đỡ?</a>
        </div>
      </header>

      
      <div className='register__container'>
        <div className='header__register-logomain'>
          <img src={logo} alt="Logo" />
        </div>
        <p>Nền tảng thương mại điển tử hàng đầu Đông Nam Á</p>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            <label htmlFor="phoneNumber">Số điện thoại</label>
            <input
              type="text"
              id="phoneNumber"
              name="phoneNumber"
              value={phoneNumber}
              onChange={handlePhoneChange}
              placeholder="Nhập số điện thoại"
            />
          </div>
          <button type="submit" className='btn-submit'>Tiếp theo</button>
        <div className="social-login">
          <button className="btn-facebook">Facebook</button>
          <button className="btn-google">Google</button>
        </div>
        <p className="terms">
          Bằng việc đăng kí, bạn đã đồng ý với <Link to="/terms">Shopee về Điều khoản sử dụng</Link> &amp;
          <Link to="/privacy-policy"> Chính sách bảo mật</Link>
        </p>
        <p className="already-account">
          Bạn đã có tài khoản? <NavLink to="/login">Đăng nhập</NavLink>
        </p>
        </form>
      </div>
    </div>
  );
};
