import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Row, Col, Button, Carousel, Statistic } from "antd";
import { EyeOutlined } from "@ant-design/icons";
import axios from "axios";
import "./productDetail.css"; // Tạo file CSS riêng cho trang chi tiết

export const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const viewIncrementedRef = useRef(false);

  // Fetch product details
  useEffect(() => {
    const fetchProductDetail = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4003/api/product/${id}`
        );
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Lỗi khi lấy chi tiết sản phẩm:", error);
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [id]);

  // Increment view count separately - only once per component mount
  useEffect(() => {
    const incrementViewCount = async () => {
      // Skip if already incremented or no product loaded yet
      if (viewIncrementedRef.current || !product) return;

      try {
        await axios.put(
          `http://localhost:4003/api/product/${id}/increment-view`
        );

        // Mark as incremented
        viewIncrementedRef.current = true;

        // Update local state with incremented view
        setProduct((prevProduct) => ({
          ...prevProduct,
          views: (prevProduct.views || 0) + 1,
        }));
      } catch (error) {
        console.error("Lỗi khi tăng lượt xem:", error);
      }
    };

    incrementViewCount();
  }, [id, product]);

  if (loading) {
    return <div>Đang tải thông tin sản phẩm...</div>;
  }

  if (!product) {
    return <div>Không tìm thấy sản phẩm!</div>;
  }

  return (
    <div className="product-detail">
      <Button className="back-button" onClick={() => navigate(-1)}>
        Quay lại
      </Button>

      <Row gutter={[24, 24]}>
        <Col xs={24} md={12}>
          <Carousel autoplay autoplaySpeed={1000}>
            {product.image.map((img, index) => (
              <div key={index}>
                <img
                  src={img}
                  alt={`${product.name} - ảnh ${index + 1}`}
                  className="product-image"
                />
              </div>
            ))}
          </Carousel>
        </Col>

        <Col xs={24} md={12}>
          <div className="product-info">
            <h1 className="product-name">{product.name}</h1>
            <div className="product-price">
              {product.price.toLocaleString("vi-VN")} đ
            </div>
            <div className="product-status">Trạng thái: {product.status}</div>

            {/* Hiển thị số lượt xem */}
            <div className="product-views">
              <Statistic
                value={product.views || 0}
                prefix={<EyeOutlined />}
                suffix="lượt xem"
              />
            </div>

            <div className="product-description">
              <h3>Mô tả sản phẩm</h3>
              <p>{product.describe}</p>
            </div>

            <div className="product-actions">
              <Button
                type="primary"
                size="large"
                onClick={async () => {
                  try {
                    const customerId = "CUSTOMER_ID"; // Thay bằng ID khách hàng hiện tại
                    const quantity = 1; // Số lượng sản phẩm muốn thêm

                    await axios.post(
                      "http://localhost:3003/api/cart/add-to-cart",
                      {
                        productId: product._id,
                        customerId,
                        quantity,
                      }
                    );

                    alert("Sản phẩm đã được thêm vào giỏ hàng!");
                  } catch (error) {
                    console.error("Lỗi khi thêm vào giỏ hàng:", error);
                    alert("Không thể thêm sản phẩm vào giỏ hàng.");
                  }
                }}
              >
                Thêm vào giỏ hàng
              </Button>
              <Button size="large">Mua ngay</Button>
            </div>
          </div>
        </Col>
      </Row>
    </div>
  );
};
