import React, { useState, useEffect } from 'react';
import './sellerform.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerForm = ({ sellerId }) => {
    const { user } = useAppContext();
    const [images, setImages] = useState([]);
    const [storeName, setStoreName] = useState('');
    const [storeAddress, setStoreAddress] = useState('');
    const [phone, setPhone] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);

    useEffect(() => {
        // Fetch seller data if editing
        const fetchSeller = async () => {
            const token = localStorage.getItem('token');
            try {
                const response = await axios.get(`http://localhost:3006/api/seller/${sellerId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                    },
                });
                const { storeName, storeAddress, phone, image } = response.data;
                setStoreName(storeName);
                setStoreAddress(storeAddress);
                setPhone(phone);
                setImages(image);
            } catch (error) {
                console.error('Error fetching seller data:', error);
            }
        };
        if (sellerId) fetchSeller();
    }, [sellerId]);

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const formData = new FormData();
        formData.append('storeName', storeName);
        formData.append('storeAddress', storeAddress);
        formData.append('phone', phone);
        images.forEach((image) => {
            formData.append('image', image);
        });

        try {
            setIsSubmitting(true);
            const res = await axios.put(`http://localhost:3006/api/seller/update/${sellerId}`, formData, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data',
                },
            });
            alert('Cập nhật shop thành công!');
        } catch (error) {
            console.error('Error updating seller:', error);
            alert('Có lỗi xảy ra khi cập nhật seller.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="form__seller-info">
            <form className="form-container-seller" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Tên Shop</label>
                    <input
                        type="text"
                        className="form-input"
                        value={storeName}
                        onChange={(e) => setStoreName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Địa chỉ</label>
                    <input
                        type="text"
                        className="form-input"
                        value={storeAddress}
                        onChange={(e) => setStoreAddress(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group small">
                    <label className="form-label">Điện thoại</label>
                    <input
                        type="text"
                        className="form-input"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {[...Array(1)].map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <button type="submit" className="submit-btn-save" disabled={isSubmitting}>
                    {isSubmitting ? 'Đang lưu...' : 'Lưu'}
                </button>
            </form>
        </div>
    );
};
