import { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';

export const AppContext = createContext();

export const AppContextProvider = ({ children }) => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [user, setUser] = useState(null);
    const [isAppInitialized, setIsAppInitialized] = useState(false);

    const fetchCategories = async () => {
        try {
            const res = await axios.get('http://localhost:3001/api/category/all');
            setCategories(res.data || []);
        } catch (error) {
            console.error('Lỗi khi lấy danh mục:', error.message);
        }
    };

    const fetchProduct = async () => {
        try {
            const res = await axios.get('http://localhost:4003/api/product/all');
            setProducts(res.data.products || []);
        } catch (error) {
            console.error('Lỗi khi lấy danh sách sản phẩm:', error.message);
        }
    };

    useEffect(() => {
        const init = async () => {
            await fetchProduct();
            await fetchCategories();

            const storedUser = localStorage.getItem('user');
            if (storedUser) {
                try {
                    setUser(JSON.parse(storedUser));
                } catch (err) {
                    console.error('Lỗi khi parse user từ localStorage:', err);
                    setUser(null);
                }
            }

            setIsAppInitialized(true); // đảm bảo luôn được gọi
        };

        init();
    }, []);

    const handleLogin = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData)); 
    };

    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    const value = {
        categories,
        setCategories,
        setProducts,
        products,
        user,
        handleLogin,
        handleLogout,
        isAppInitialized,
    };

    return (
        <AppContext.Provider value={value}>
            {children}
        </AppContext.Provider>
    );
};

export const useAppContext = () => useContext(AppContext);
