import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import { Link, useNavigate } from 'react-router-dom';
import './cart.css';
import axios from 'axios';

export const Cart = () => {
  const { cartItems, setCartItems, customer, fetchCartItems } = useAppContext(); // Lấy fetchCartItems từ context
  const navigate = useNavigate();
  const [selectedItems, setSelectedItems] = useState([]);
  const [isDeleting, setIsDeleting] = useState(false); // Thêm state để theo dõi quá trình xóa

  useEffect(() => {
    // Cập nhật selectedItems khi cartItems thay đổi
    setSelectedItems(cartItems.filter(item => selectedItems.includes(item.productId)).map(item => item.productId));
  }, [cartItems, selectedItems]);

  const getCartKey = () => {
    const userId = customer?._id || customer?.id;
    return userId ? `cartItems_user_${userId}` : 'cartItems_guest';
  };

  const updateCartInLocalStorage = (newCartItems) => {
    localStorage.setItem(getCartKey(), JSON.stringify(newCartItems));
    console.log('Cart Component - updateCartInLocalStorage:', newCartItems);
    setCartItems(newCartItems);
  };

  const handleSelectItem = (productId) => {
    setSelectedItems(prev =>
      prev.includes(productId)
        ? prev.filter(id => id !== productId)
        : [...prev, productId]
    );
  };

  const handleSelectAll = () => {
    if (selectedItems.length === cartItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item.productId));
    }
  };

  const handleIncrease = async (productId) => {
    try {
      const updatedCartItems = cartItems.map(item =>
        item.productId === productId
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      updateCartInLocalStorage(updatedCartItems);

      const updatedItemOnServer = updatedCartItems.find(item => item.productId === productId);
      if (customer && updatedItemOnServer?._id) {
        await axios.put(`http://localhost:3003/api/cart/update/${updatedItemOnServer._id}`,
          { quantity: updatedItemOnServer.quantity },
          {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }
        );
      }

    } catch (error) {
      console.log('Lỗi khi cập nhật giỏ hàng:', error);
    }
  };

  const handleDecrease = async (productId) => {
    try {
      const updatedCartItems = cartItems.map(item =>
        item.productId === productId && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      );
      updateCartInLocalStorage(updatedCartItems);

      const updatedItemOnServer = updatedCartItems.find(item => item.productId === productId);
      if (customer && updatedItemOnServer?._id) {
        await axios.put(`http://localhost:3003/api/cart/update/${updatedItemOnServer._id}`,
          { quantity: updatedItemOnServer.quantity },
          {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }
        );
      }
    } catch (error) {
      console.log('Lỗi khi cập nhật giỏ hàng:', error);
    }
  };

  const handleDelete = async (productId) => {
    try {
      if (window.confirm("Bạn có chắc chắn muốn xóa sản phẩm này?")) {
        setIsDeleting(true); // Bắt đầu quá trình xóa
        const itemToDelete = cartItems.find(item => item.productId === productId);

        if (customer && itemToDelete?._id) {
          await axios.delete(`http://localhost:3003/api/cart/delete/${itemToDelete._id}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          });
          console.log('Sản phẩm đã xóa khỏi server');
          // Sau khi xóa thành công trên server, gọi fetchCartItems để cập nhật lại giỏ hàng
          await fetchCartItems(customer._id || customer.id);
        } else {
          // Nếu không đăng nhập, chỉ cập nhật local
          const updatedCartItems = cartItems.filter(item => item.productId !== productId);
          updateCartInLocalStorage(updatedCartItems);
          setSelectedItems(prev => prev.filter(id => id !== productId));
        }
      }
    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm:', error);
      // Xử lý lỗi nếu cần, ví dụ hiển thị thông báo cho người dùng
    } finally {
      setIsDeleting(false); // Kết thúc quá trình xóa
    }
  };


  const handleBuyNow = () => {
    const updatedCart = cartItems.map(item => ({
      ...item,
      selected: selectedItems.includes(item.productId),
    }));
    setCartItems(updatedCart);
    navigate('/order');
  };

  const totalSelectedPrice = selectedItems.reduce((total, productId) => {
    const item = cartItems.find(item => item.productId === productId);
    return total + (item?.productPrice || 0) * (item?.quantity || 0);
  }, 0);

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={handleSelectAll}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={index}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.productId)}
                  onChange={() => handleSelectItem(item.productId)}
                />
                <img src={item.productImage} alt={item.productName} />
                <div className="cart-item-info">
                  <h4>{item.productName}</h4>
                  <p>Giá: đ{item.productPrice.toLocaleString()}</p>

                  <div className="quantity-control">
                    <button onClick={() => handleDecrease(item.productId)}>-</button>
                    <input type="text" value={item.quantity} readOnly />
                    <button onClick={() => handleIncrease(item.productId)}>+</button>
                  </div>

                  <p>Thành tiền: đ{(item.productPrice * item.quantity).toLocaleString()}</p>

                  <button
                    className="btn-delete"
                    onClick={() => handleDelete(item.productId)}
                    disabled={isDeleting} // Vô hiệu hóa nút xóa trong quá trình xóa
                  >
                    {isDeleting ? 'Đang xóa...' : 'Xóa'}
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={handleSelectAll}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>Tổng thanh toán: <span>đ{totalSelectedPrice.toLocaleString()}</span></p>
              <button
                className="btn-buy"
                disabled={selectedItems.length === 0}
                onClick={handleBuyNow}
              >
                Đặt hàng
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
