import React, { useState, useEffect } from 'react';
import './sellershop.css';

export const SellerShop = ({ sellerId }) => {
  const [seller, setSeller] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSeller = async () => {
      try {
        const response = await fetch(`http://localhost:3006/api//seller/${sellerId}`);
        if (!response.ok) {
          throw new Error('Seller not found');
        }
        const data = await response.json();
        setSeller(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchSeller();
  }, [sellerId]);

  if (loading) {
    return <p>Loading seller information...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="shop-container">
      {seller ? (
        <>
          <div className="shop-header">
            <div className="shop-image">
              {seller.image && seller.image.length > 0 && (
                <img src={seller.image[0]} alt="Shop" />
              )}
            </div>
            <div className="shop-info">
              <h1>{seller.storeName}</h1>
              <p>{seller.storeAddress}</p>
              <p>Phone: {seller.phone}</p>
            </div>
          </div>

          <div className="shop-products">
            <h2>Our Products</h2>
            <div className="product-list">
              {seller.products && seller.products.length > 0 ? (
                seller.products.map((product) => (
                  <div className="product-item" key={product._id}>
                    <img src={product.image} alt={product.name} />
                    <div className="product-details">
                      <h3>{product.name}</h3>
                      <p>{product.description}</p>
                      <p>${product.price}</p>
                      <button className="add-to-cart">Add to Cart</button>
                    </div>
                  </div>
                ))
              ) : (
                <p>No products available</p>
              )}
            </div>
          </div>
        </>
      ) : (
        <p>No seller found</p>
      )}
    </div>
  );
};
