import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [shippingInfo, setShippingInfo] = useState({
    name: '',
    phone: '',
    province: '',
    address: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('cod');
  const userData = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchCart = async () => {
      const res = await axios.get(`http://localhost:3003/api/cart/user/${userData.id}`);
      setCartItems(res.data);
    };
    fetchCart();
  }, [userData.id]);

  const totalOrder = cartItems.reduce((sum, item) => {
    return sum + item.quantity * item.productId.price;
  }, 0);

  const handleOrder = async () => {
    try {
      // 1. Tạo đơn hàng
      const orderRes = await axios.post('http://localhost:4000/api/order/create', {
        totalOrder,
        status: 'Pending',
        shippingInfo,
        customerId: userData.id,
        userId: userData.id,
        paymentId: paymentMethod, // hoặc mapping với payment collection nếu có
        transportId: 'transport123' // ID mẫu, thay thế nếu có dữ liệu thật
      });

      const orderId = orderRes.data.order._id;

      // 2. Tạo từng chi tiết đơn hàng
      for (const item of cartItems) {
        await axios.post('http://localhost:4001/api/orderDetails/create', {
          orderId,
          productId: item.productId._id,
          quantity: item.quantity,
          totalPrice: item.quantity * item.productId.price
        });
      }

      // 3. Xóa giỏ hàng
      for (const item of cartItems) {
        await axios.delete(`http://localhost:3003/api/cart/deleteCart/${item._id}`);
      }

      alert('Đặt hàng thành công!');
      navigate('/'); // quay về trang chủ
    } catch (error) {
      console.error('Lỗi đặt hàng:', error);
      alert('Đặt hàng thất bại!');
    }
  };

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input type="text" placeholder="Họ và tên" required onChange={e => setShippingInfo({ ...shippingInfo, name: e.target.value })} />
          <input type="number" placeholder="Phone" required onChange={e => setShippingInfo({ ...shippingInfo, phone: e.target.value })} />
          <input type="text" placeholder="Tỉnh / Thành phố" required onChange={e => setShippingInfo({ ...shippingInfo, province: e.target.value })} />
          <input type="text" placeholder="Địa chỉ chi tiết" required onChange={e => setShippingInfo({ ...shippingInfo, address: e.target.value })} />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        {['cod', 'bank', 'credit'].map(method => (
          <label key={method}>
            <input type="radio" name="paymentMethod" value={method} checked={paymentMethod === method} onChange={(e) => setPaymentMethod(e.target.value)} />
            {method === 'cod' ? 'Thanh toán khi nhận hàng (COD)' : method === 'bank' ? 'Ngân hàng' : 'Thẻ tín dụng'}
          </label>
        ))}
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {cartItems.map((item, idx) => (
          <div className="summary-item" key={idx}>
            <img src={item.productId.image[0]} alt="" />
            <div>
              <p>{item.productId.name}</p>
              <p>Số lượng: {item.quantity}</p>
              <p>Giá: {item.productId.price.toLocaleString()}đ</p>
            </div>
          </div>
        ))}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>{totalOrder.toLocaleString()}đ</p>
        <button className="btn-confirm" onClick={handleOrder}>
          Đặt hàng
        </button>
      </div>
    </div>
  );
};
