import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Col, Row } from 'antd';
import './productCategory.css'; 

export const ProductCategory = () => {
    const { category } = useParams(); 
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProductsByCategory = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/by-category/${category}`);
                setProducts(res.data.products || []);
            } catch (err) {
                console.error('Lỗi khi lấy sản phẩm theo danh mục:', err.message);
            }
        };

        fetchProductsByCategory();
    }, [category]);

    return (
        <section className="product-category">
            <h2>Sản phẩm trong danh mục: {category}</h2>
            <Row>
                <Col span={24}>
                    <div className="product-category__list">
                        {products.map((item) => (
                            <div key={item._id} className="product__item">
                                <img src={item.image?.[0]} alt={item.name} />
                                <p>{item.name}</p>
                                <p>{item.price.toLocaleString()}₫</p>
                            </div>
                        ))}
                    </div>
                </Col>
            </Row>
        </section>
    );
};
