import React, { useState, useEffect } from 'react';
import './customerprofile.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const CustomerProfile = () => {
    const { userId, loadingUser, userError } = useAppContext();
    const [formData, setFormData] = useState({
        fullName: '',
        phone: '',
        address: '',
        birthday: '',
        gender: '',
        userId: userId, // Khởi tạo userId từ context
    });

    useEffect(() => {
        // Cập nhật formData.userId khi userId từ context thay đổi
        setFormData(prevFormData => ({
            ...prevFormData,
            userId: userId,
        }));
    }, [userId]); // Dependency array chứa userId, effect sẽ chạy lại khi userId thay đổi

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        const token = localStorage.getItem('authToken');
        const response = await axios.post(
            'http://localhost:3002/api/customer/create',
            formData,
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            }
        );
        console.log('Customer saved successfully:', response.data);
        setFormData({
            fullName: '',
            phone: '',
            address: '',
            birthday: '',
            gender: '',
            userId: userId,
        });
        alert('Thông tin khách hàng đã được lưu!');
    } catch (error) {
        console.error('Error saving customer:', error.message);
        alert('Đã có lỗi xảy ra khi lưu thông tin khách hàng.');
    }
};


    if (loadingUser) {
        return <div>Đang tải thông tin người dùng...</div>;
    }

    if (userError) {
        return <div>Lỗi khi tải thông tin người dùng. Vui lòng thử lại sau.</div>;
    }

    return (
        <div className="customer-profile">
            <h2>Thông Tin Khách Hàng</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Họ và tên</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Điện Thoại</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Địa chỉ</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>

                <div className="form-group">
                    <label>Sinh Nhật</label>
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                        className="input-date"
                    />
                </div>

                <div className="form-group">
                    <label>Giới tính</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-gender"
                    >
                        <option value="">Chọn giới tính</option>
                        <option value="male">Nam</option>
                        <option value="female">Nữ</option>
                        <option value="other">Khác</option>
                    </select>
                </div>
                <button type="submit" className='updateProfile'>
                    Lưu
                </button>
            </form>
        </div>
    );
};
