import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './payment.css';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';

export const Payment = () => {
  const { cartItems, products, customer } = useAppContext();
  const [paymentMethod, setPaymentMethod] = useState('cod');

  // 🏠 Form địa chỉ
  const [fullName, setFullName] = useState('');
  const [phone, setPhone] = useState('');
  const [city, setCity] = useState('');
  const [detailAddress, setDetailAddress] = useState('');

  const selectedItems = cartItems.filter(item => item.selected);
  const getProductById = (id) => products.find((p) => p._id === id); // sửa lại nếu dùng _id

  const totalPrice = selectedItems.reduce((acc, item) => {
    const prod = getProductById(item.productId);
    if (!prod) return acc;
    return acc + prod.discounted_price * item.quantity;
  }, 0);

  const handlePaymentChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  // 🧾 Hàm gọi API tạo địa chỉ
  const handleOrder = async () => {
    if (!customer?._id) {
      alert('Bạn cần đăng nhập để đặt hàng.');
      return;
    }

    try {
      await axios.post('http://localhost:3003/api/address/create', {
        customerId: customer._id,
        city,
        describe: `${fullName}, ${phone}, ${detailAddress}`
      });

      alert('Đặt hàng thành công!');

      // 👉 redirect hoặc reset form ở đây nếu muốn
    } catch (error) {
      console.error('Lỗi khi tạo địa chỉ:', error.message);
      alert('Có lỗi xảy ra khi đặt hàng.');
    }
  };

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input type="text" placeholder="Họ và tên" required value={fullName} onChange={(e) => setFullName(e.target.value)} />
          <input type="text" placeholder="Số điện thoại" required value={phone} onChange={(e) => setPhone(e.target.value)} />
          <input type="text" placeholder="Tỉnh / Thành phố" required value={city} onChange={(e) => setCity(e.target.value)} />
          <input type="text" placeholder="Địa chỉ chi tiết" required value={detailAddress} onChange={(e) => setDetailAddress(e.target.value)} />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        <div className="payment-options">
          <label>
            <input
              type="radio"
              name="paymentMethod"
              value="cod"
              checked={paymentMethod === 'cod'}
              onChange={handlePaymentChange}
            />
            Thanh toán khi nhận hàng (COD)
          </label>
          <label>
            <input
              type="radio"
              name="paymentMethod"
              value="bank"
              checked={paymentMethod === 'bank'}
              onChange={handlePaymentChange}
            />
            Chuyển khoản ngân hàng
          </label>
          <label>
            <input
              type="radio"
              name="paymentMethod"
              value="credit"
              checked={paymentMethod === 'credit'}
              onChange={handlePaymentChange}
            />
            Thẻ tín dụng / Ghi nợ
          </label>
        </div>
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {selectedItems.map((item, index) => {
          const prod = getProductById(item.productId);
          if (!prod) return null;
          return (
            <div className="summary-item" key={index}>
              <img src={prod.img[0]} alt={prod.name} />
              <div>
                <p>{prod.name}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Giá: đ{(prod.discounted_price * item.quantity).toLocaleString('vi-VN')}</p>
              </div>
            </div>
          );
        })}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
        <button className="btn-confirm" onClick={handleOrder}>
          Đặt hàng
        </button>
      </div>
    </div>
  );
};
