import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { PurchaseBenefits } from '../PurchaseBenefits/PurchaseBenefits';
import { SellerShop } from '../Seller/SellerShop';

export const ProductDetails = () => {
  const { id } = useParams(); // lấy id từ URL
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/products/${id}`);
        setProduct(res.data);
      } catch (error) {
        console.error('Error fetching product:', error);
        navigate('/not-found'); // điều hướng nếu lỗi
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id, navigate]);

  if (loading) return <p>Đang tải...</p>;
  if (!product) return <p>Không tìm thấy sản phẩm.</p>;

  return (
    <div className="product-details-container">
      <div className="product-images">
        {product.image?.map((imgUrl, index) => (
          <img key={index} src={imgUrl} alt={`Product ${index}`} />
        ))}
      </div>
      <div className="product-info">
        <h1>{product.name}</h1>
        <p className="price">{product.price?.toLocaleString()} VNĐ</p>
        <p className="description">{product.describe}</p>
        <p className="status">Trạng thái: {product.status}</p>
        <p className="category">Danh mục: {product.categoryId?.name}</p>

        {/* Component thêm như lợi ích hoặc shop */}
        <PurchaseBenefits />
        <SellerShop sellerId={product.sellerId} />
      </div>
    </div>
  );
};































