import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './order.css';
import { useAppContext } from '../../context/AppContext';
import { Link } from 'react-router-dom';

export const Order = () => {
    const { cartItems, products } = useAppContext();
    const [paymentMethod, setPaymentMethod] = useState('cod');
    const [paymentMethods, setPaymentMethods] = useState([]);
    const [address, setAddress] = useState({
        name: '',
        phone: '',
        city: '',
        detailedAddress: ''
    });

    // Load payment methods from backend
    useEffect(() => {
        const fetchPaymentMethods = async () => {
            try {
                const res = await axios.get('http://localhost:3008/api/paymentMethod/all');
                setPaymentMethods(res.data);
            } catch (err) {
                console.error('Error fetching payment methods:', err);
            }
        };
        fetchPaymentMethods();
    }, []);

    // Lọc sản phẩm được chọn để mua
    const selectedItems = cartItems.filter(item => item.selected);

    // Tìm sản phẩm từ danh sách products
    const getProductById = (id) => products.find((p) => p._id === id);

    // Tính tổng tiền
    const totalPrice = selectedItems.reduce((acc, item) => {
        const prod = getProductById(item.productId);
        if (!prod) return acc;
        const price = prod.discounted_price || prod.price || 0;
        return acc + price * item.quantity;
    }, 0);

    // Xử lý thay đổi phương thức thanh toán
    const handlePaymentChange = (e) => {
        setPaymentMethod(e.target.value);
    };

    // Lấy ID của phương thức thanh toán đã chọn
    const getSelectedPaymentMethodId = () => {
        const selected = paymentMethods.find(pm => pm.namePaymentMethod.toLowerCase() === paymentMethod);
        return selected ? selected._id : null;
    };

    // Xử lý khi người dùng nhấn "Đặt hàng"
    const handleOrderSubmit = async () => {
        const selectedPaymentMethodId = getSelectedPaymentMethodId();
        if (!selectedPaymentMethodId) {
            alert('Vui lòng chọn phương thức thanh toán hợp lệ');
            return;
        }

        try {
            const res = await axios.post('/api/payment', {
                paymentDate: new Date(),
                amount: totalPrice,
                paymentMethodId: selectedPaymentMethodId,
                paymentStatusId: 'pending' // bạn có thể điều chỉnh theo trạng thái mặc định
            });

            alert('Đặt hàng thành công!');
            // Redirect hoặc xử lý thêm
        } catch (error) {
            console.error('Lỗi khi đặt hàng:', error);
            alert('Có lỗi xảy ra khi thanh toán');
        }
    };

    return (
        <div className="payment-container">
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form">
                    <input
                        type="text"
                        placeholder="Họ và tên"
                        required
                        value={address.name}
                        onChange={(e) => setAddress({ ...address, name: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Số điện thoại"
                        required
                        value={address.phone}
                        onChange={(e) => setAddress({ ...address, phone: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Tỉnh / Thành phố"
                        required
                        value={address.city}
                        onChange={(e) => setAddress({ ...address, city: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Địa chỉ chi tiết"
                        required
                        value={address.detailedAddress}
                        onChange={(e) => setAddress({ ...address, detailedAddress: e.target.value })}
                    />
                </form>
            </div>

            <div className="payment-method-section">
                <h3>Phương thức thanh toán</h3>
                <div className="payment-options">
                    {paymentMethods.map((method) => (
                        <label key={method._id}>
                            <input
                                type="radio"
                                name="paymentMethod"
                                value={method.namePaymentMethod.toLowerCase()}
                                checked={paymentMethod === method.namePaymentMethod.toLowerCase()}
                                onChange={handlePaymentChange}
                            />
                            {method.namePaymentMethod}
                        </label>
                    ))}
                </div>
            </div>

            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {selectedItems.map((item, index) => {
                    const prod = getProductById(item.productId);
                    if (!prod) return null;

                    const price = prod.discounted_price || prod.price || 0;
                    return (
                        <div className="summary-item" key={index}>
                            <img
                                src={prod.img?.[0] || prod.image?.[0] || 'fallback.jpg'}
                                alt={prod.name}
                            />
                            <div>
                                <p>{prod.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: đ{(price * item.quantity).toLocaleString('vi-VN')}</p>
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
                <button className="btn-confirm" onClick={handleOrderSubmit}>
                    Đặt hàng
                </button>
            </div>
        </div>
    );
};
