import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams, useNavigate } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);
    const [shopInfo, setShopInfo] = useState(null);
    const navigate = useNavigate();

    // L·∫•y th√¥ng tin shop
    const fetchShopInfo = async () => {
        try {
            const res = await axios.get(`http://localhost:4003/api/seller/${sellerId}`);  // API l·∫•y th√¥ng tin shop
            console.log("Th√¥ng tin shop tr·∫£ v·ªÅ:", res.data);
            setShopInfo({
                name: res.data.storeName,
                avatar: res.data.image || "/default-avatar.png", // N·∫øu kh√¥ng c√≥ ·∫£nh th√¨ d√πng ·∫£nh m·∫∑c ƒë·ªãnh
                storeAddress: res.data.storeAddress,
                phone: res.data.phone,
            });
        } catch (error) {
            console.error('L·ªói khi l·∫•y th√¥ng tin shop:', error);
        }
    };

    // L·∫•y s·∫£n ph·∫©m c·ªßa shop
    const fetchProducts = async () => {
        try {
            const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
            console.log("D·ªØ li·ªáu s·∫£n ph·∫©m tr·∫£ v·ªÅ:", res.data);
            setProducts(res.data);
        } catch (error) {
            console.error('L·ªói khi l·∫•y s·∫£n ph·∫©m:', error);
        }
    };

    // Ch·∫°y h√†m fetch khi sellerId thay ƒë·ªïi
    useEffect(() => {
        fetchShopInfo();
        fetchProducts();
    }, [sellerId]);

    if (!shopInfo) {
        return <div>Loading shop info...</div>; // Hi·ªÉn th·ªã khi th√¥ng tin shop ch∆∞a ƒë∆∞·ª£c t·∫£i xong
    }

    return (
        <div className="shop-page">
            <div className="shop-header">
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src={shopInfo.avatar}
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">{shopInfo.name}</h2>
                        <p className="shop-online">Online: 7 minutes ago</p>
                        <div className="shop-buttons">
                            <button className="view-btn" onClick={() => navigate(`/shop/${sellerId}`)}>üè™ Xem Shop</button>
                        </div>
                    </div>
                </div>

                <div className="shop-info-right">
                    <div className="shop-stat">
                        <p className="stat-title">ƒê·ªãa ch·ªâ: {shopInfo.storeAddress}</p>
                        <p className="stat-title">S·ªë ƒëi·ªán tho·∫°i: {shopInfo.phone}</p>
                    </div>
                </div>
            </div>

            {/* Hi·ªÉn th·ªã c√°c s·∫£n ph·∫©m c·ªßa shop */}
            <div className="shop-products">
                {products.length === 0 ? (
                    <p>Loading products...</p>
                ) : (
                    products.map((product) => (
                        <div key={product._id} className="product-item">
                            <img
                                src={product.images[0] || "/default-image.png"}  // D√πng ·∫£nh m·∫∑c ƒë·ªãnh n·∫øu s·∫£n ph·∫©m kh√¥ng c√≥ ·∫£nh
                                alt={product.name}
                                className="product-image"
                            />
                            <div className="product-info">
                                <h3 className="product-name">{product.name}</h3>
                                <p className="product-category">{product.categoryId.name}</p>
                                <p className="product-price">${product.price}</p>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};
