import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams, useNavigate } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);
    const [shopInfo, setShopInfo] = useState(null);
    const navigate = useNavigate();

    // Lấy thông tin shop
    const fetchShopInfo = async () => {
        try {
            const res = await axios.get(`http://localhost:4003/api/seller/${sellerId}`);  // API lấy thông tin shop
            console.log("Thông tin shop trả về:", res.data);
            setShopInfo({
                name: res.data.storeName,
                avatar: res.data.image || "/default-avatar.png", // Nếu không có ảnh thì dùng ảnh mặc định
                storeAddress: res.data.storeAddress,
                phone: res.data.phone,
            });
        } catch (error) {
            console.error('Lỗi khi lấy thông tin shop:', error);
        }
    };

    // Lấy sản phẩm của shop
    const fetchProducts = async () => {
        try {
            const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
            console.log("Dữ liệu sản phẩm trả về:", res.data);
            setProducts(res.data);
        } catch (error) {
            console.error('Lỗi khi lấy sản phẩm:', error);
        }
    };

    // Chạy hàm fetch khi sellerId thay đổi
    useEffect(() => {
        fetchShopInfo();
        fetchProducts();
    }, [sellerId]);

    if (!shopInfo) {
        return <div>Loading shop info...</div>; // Hiển thị khi thông tin shop chưa được tải xong
    }

    return (
        <div className="shop-page">
            <div className="shop-header">
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src={shopInfo.avatar}
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">{shopInfo.name}</h2>
                        <p className="shop-online">Online: 7 minutes ago</p>
                        <div className="shop-buttons">
                            <button className="view-btn" onClick={() => navigate(`/shop/${sellerId}`)}>🏪 Xem Shop</button>
                        </div>
                    </div>
                </div>

                <div className="shop-info-right">
                    <div className="shop-stat">
                        <p className="stat-title">Địa chỉ: {shopInfo.storeAddress}</p>
                        <p className="stat-title">Số điện thoại: {shopInfo.phone}</p>
                    </div>
                </div>
            </div>

            {/* Hiển thị các sản phẩm của shop */}
            <div className="shop-products">
                {products.length === 0 ? (
                    <p>Loading products...</p>
                ) : (
                    products.map((product) => (
                        <div key={product._id} className="product-item">
                            <img
                                src={product.images[0] || "/default-image.png"}  // Dùng ảnh mặc định nếu sản phẩm không có ảnh
                                alt={product.name}
                                className="product-image"
                            />
                            <div className="product-info">
                                <h3 className="product-name">{product.name}</h3>
                                <p className="product-category">{product.categoryId.name}</p>
                                <p className="product-price">${product.price}</p>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};
