import React from 'react';
import { useAppContext } from '../../context/AppContext';
import { Link } from 'react-router-dom';
import './cart.css';

export const Cart = () => {
  const { cartItems, product } = useAppContext();

  const getProductById = (id) => {
    return product.find((item) => item.id === id);
  };

  const totalPrice = cartItems.reduce((acc, item) => {
    const prod = getProductById(item.productId);
    return acc + (prod?.discounted_price || 0) * item.quantity;
  }, 0);

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/product" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <div className="cart-items">
          {cartItems.map((item, index) => {
            const prod = getProductById(item.productId);
            if (!prod) return null;

            return (
              <div className="cart-item" key={index}>
                <img src={prod.img[0]} alt={prod.name} />
                <div className="cart-item-info">
                  <h4>{prod.name}</h4>
                  <p>Giá: đ{prod.discounted_price}</p>
                  <p>Số lượng: {item.quantity}</p>
                  <p>Thành tiền: đ{prod.discounted_price * item.quantity}</p>
                </div>
              </div>
            );
          })}

          <div className="cart-total">
            <h3>Tổng cộng: đ{totalPrice}</h3>
            <button className="btn-checkout">Tiến hành thanh toán</button>
          </div>
        </div>
      )}
    </div>
  );
};
