import { createContext, useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
export const AppContext = createContext();
export const AppContextProvider = ({ children }) => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [userId, setUserId] = useState(null);
    const [loadingUser, setLoadingUser] = useState(true); // Thêm state loading
    const [userError, setUserError] = useState(null);
    const fetchCategories = async () => {
        try {
            const res = await axios.get('http://localhost:3001/api/category/all');
            setCategories(res.data || []);
        } catch (error) {
            console.error('Lỗi khi lấy danh mục:', error.message);
        }
    };
    const fetchLoggedInUser = async () => {
        setLoadingUser(true);
        setUserError(null);
        try {

            const token = localStorage.getItem('authToken'); 
            const response = await axios.get('http://localhost:3002/api/auth/me', {
                headers: {
                    'Authorization': `Bearer ${token}` // Nếu cần xác thực
                }
            });
            const userData = response.data.user; 
            if (userData && userData._id) {
                setUserId(userData._id);
            }
        } catch (error) {
            console.error('Lỗi khi lấy thông tin người dùng:', error.message);
            setUserError(error);
  
        } finally {
            setLoadingUser(false);
        }
    };
    const fetchProduct = async () => {
        try {
            const res = await axios.get('http://localhost:4003/api/product/all');
            setProducts(res.data.products || []);
        } catch (error) {
            console.error('Lỗi khi lấy danh sách sản phẩm:', error.message);
        }
    };
    useEffect(() => {
        fetchProduct();
        fetchCategories();
          fetchLoggedInUser();


    }, []);
    const value = {
        categories,
        setCategories,
        setProducts,
        products,
        loadingUser,
        userError, 
    };
    return (
        <AppContext.Provider value={value}>
            {children}
        </AppContext.Provider>
    );

};

export const useAppContext = () => useContext(AppContext);
