import React, { useState, useEffect } from 'react';
import './customerprofile.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const CustomerProfile = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        phone: '',
        address: '',
        birthday: '',
        gender: '',
    });
    const { user, isAppInitialized } = useAppContext();
    const [customerData, setCustomerData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchCustomerData = async () => {
            if (user && user.id) {
                setLoading(true);
                setError('');
                try {
                    const response = await axios.put(`http://localhost:3002/api/customer/update/${customerData._id}`, formData); // API endpoint để lấy customer theo userId
                    if (response.data) {
                        setCustomerData(response.data);
                        setFormData(response.data); // Đổ dữ liệu vào form
                    } else {
                        // Nếu không tìm thấy customer cho user này, có thể cho phép tạo mới
                        console.log('No customer found for this user.');
                    }
                } catch (err) {
                    setError('Failed to load customer data.');
                    console.error('Error fetching customer data:', err.message);
                } finally {
                    setLoading(false);
                }
            }
        };

        if (isAppInitialized && user?.id) {
            fetchCustomerData();
        }
    }, [isAppInitialized, user?.id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (customerData?._id) { // Kiểm tra xem đã có customerData và có _id hay chưa (đã tồn tại)
            try {
                const response = await axios.put(`http://localhost:3002/api/customer/user/${user.id}`, formData); // Gọi API PUT để cập nhật
                console.log('Customer updated:', response.data);
                alert('Thông tin khách hàng đã được cập nhật thành công!');
                // Có thể cập nhật lại state customerData nếu cần
                setCustomerData(response.data.customer);
            } catch (error) {
                console.error('Error updating customer:', error.message);
                setError('Failed to update customer information.');
            }
        } else if (user?.id) {
            // Nếu không có customerData (có thể là lần đầu tạo)
            try {
                const response = await axios.post('http://localhost:3002/api/customer/create', {
                    ...formData,
                    userId: user.id,
                });
                console.log('Customer created:', response.data);
                alert('Thông tin khách hàng đã được lưu thành công!');
                setCustomerData(response.data.customer); // Cập nhật customerData sau khi tạo mới
            } catch (error) {
                console.error('Error creating customer:', error.message);
                setError('Failed to create customer information.');
            }
        } else {
            console.error('User không tồn tại hoặc chưa đăng nhập.');
            setError('User not found or not logged in.');
        }
    };

    if (!isAppInitialized) {
        return <div>Đang tải thông tin người dùng...</div>;
    }

    if (!user || !user.id) {
        return <div>Không tìm thấy người dùng. Vui lòng đăng nhập.</div>;
    }

    if (loading) {
        return <div>Đang tải thông tin khách hàng...</div>;
    }

    if (error) {
        return <div>Lỗi: {error}</div>;
    }

    return (
        <div className="customer-profile">
            <h2>Thông Tin Khách Hàng</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Họ và tên</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Điện Thoại</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Địa chỉ</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>

                <div className="form-group">
                    <label>Sinh Nhật</label>
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                        className="input-date"
                    />
                </div>

                <div className="form-group">
                    <label>Giới tính</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-gender"
                    >
                        <option value="">Chọn giới tính</option>
                        <option value="male">Nam</option>
                        <option value="female">Nữ</option>
                        <option value="other">Khác</option>
                    </select>
                </div>
                <button type="submit" className='updateProfile'>
                    {customerData?._id ? 'Lưu Thay Đổi' : 'Lưu Thông Tin'}
                </button>
            </form>
        </div>
    );
};
