import React from 'react';
import { useParams } from 'react-router-dom';
import { useAppContext } from '../../context/AppContext';
import categoryData from '../../assets/data/category'; // nhập danh sách category

export const ProductCategory = () => {
    const { category } = useParams(); // category lấy từ URL
    const { product } = useAppContext();

    // Hàm chuyển chuỗi về slug giống như bạn dùng trong component Category
    const toSlug = (str) =>
        str.normalize("NFD")
            .replace(/[\u0300-\u036f]/g, "")
            .replace(/đ/g, "d")
            .replace(/Đ/g, "d")
            .replace(/[^a-zA-Z0-9\s]/g, "")
            .trim()
            .toLowerCase()
            .replace(/\s+/g, "-");

    // Tìm category theo slug
    const searchCategory = categoryData.find(item => toSlug(item.name) === category);

    return (
        <div>
            {searchCategory ? (
                <div>
                    <h2>{searchCategory.name.toUpperCase()}</h2>
                    {/* render danh sách sản phẩm ở đây nếu cần */}
                </div>
            ) : (
                <p>Không tìm thấy danh mục</p>
            )}
        </div>
    );
};
