import React, { useEffect, useState } from 'react'; 
import { Link } from 'react-router-dom'; 
import './cart.css'; 
import axios from 'axios';

export const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [totalSelectedPrice, setTotalSelectedPrice] = useState(0);
  const [loading, setLoading] = useState(true);

  // Lấy dữ liệu giỏ hàng từ backend
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const userData = JSON.parse(localStorage.getItem('user'));
        const response = await axios.get(`http://localhost:3003/api/cart/user/${userData.id}`);
        setCartItems(response.data);
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  // Tính tổng giá trị các sản phẩm được chọn
  useEffect(() => {
    const total = cartItems
      .filter(item => selectedItems.includes(item.productId))
      .reduce((sum, item) => sum + item.productPrice * item.quantity, 0);
    setTotalSelectedPrice(total);
  }, [selectedItems, cartItems]);

  const handleSelectAll = () => {
    if (selectedItems.length === cartItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item.productId));
    }
  };

  const handleSelectItem = (productId) => {
    setSelectedItems(prevState =>
      prevState.includes(productId)
        ? prevState.filter(id => id !== productId)
        : [...prevState, productId]
    );
  };

  const handleDecrease = (productId) => {
    // Giảm số lượng sản phẩm (chưa cập nhật backend)
  };

  const handleIncrease = (productId) => {
    // Tăng số lượng sản phẩm (chưa cập nhật backend)
  };

  const handleDelete = (productId) => {
    // Xóa sản phẩm khỏi giỏ hàng (chưa cập nhật backend)
  };

  const handleBuyNow = () => {
    // Xử lý việc mua hàng
  };

  if (loading) {
    return <p>Đang tải...</p>;
  }

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={handleSelectAll}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={index}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.productId)}
                  onChange={() => handleSelectItem(item.productId)}
                />
                <img src={item.productImage} alt={item.productName} />
                <div className="cart-item-info">
                  <h4>{item.productName}</h4>
                  <p>Giá: đ{item.productPrice.toLocaleString()}</p>

                  <div className="quantity-control">
                    <button onClick={() => handleDecrease(item.productId)}>-</button>
                    <input type="text" value={item.quantity} readOnly />
                    <button onClick={() => handleIncrease(item.productId)}>+</button>
                  </div>

                  <p>Thành tiền: đ{(item.productPrice * item.quantity).toLocaleString()}</p>

                  <button className="btn-delete" onClick={() => handleDelete(item.productId)}>
                    Xóa
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={handleSelectAll}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>Tổng thanh toán: <span>đ{totalSelectedPrice.toLocaleString()}</span></p>
              <button
                className="btn-buy"
                disabled={selectedItems.length === 0}
                onClick={handleBuyNow}
              >
                Mua hàng
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
