import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './shoppages.css';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);

    useEffect(() => {
        console.log('SellerId:', sellerId); // Kiểm tra giá trị sellerId

        const fetchProducts = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
                setProducts(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy sản phẩm:', error);
            }
        };

        fetchProducts();
    }, [sellerId]);
    return (
        <div className="shop-page">
            <h2>Danh sách sản phẩm của shop</h2>
            <div className="product-list">
                {products.map((item) => (
                    <div key={item._id} className="product-card">
                        <img src={item.images[0]} alt={item.name} />
                        <h4>{item.name}</h4>
                        <p>{item.price} VND</p>
                        <p>Danh mục: {item.categoryId?.name}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};
