import React, { useState } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
const AddProducts = () => {
  const [productData, setProductData] = useState({
    name: '',
    description: '',
    category: '',
    status: 'còn hàng',
    price: '',
    quantity: '',
  });

  const [images, setImages] = useState([]);

  const handleChange = (e) => {
    setProductData({
      ...productData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageChange = (e) => {
    setImages(Array.from(e.target.files));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('name', productData.name);
      formData.append('description', productData.description);
      formData.append('category', productData.category);
      formData.append('status', productData.status);
      formData.append('price', productData.price);
      formData.append('quantity', productData.quantity);

      // Lấy userId và sellerId từ localStorage
      const userId = localStorage.getItem('userId');
      const sellerId = localStorage.getItem('sellerId');

      formData.append('userId', userId);
      formData.append('sellerId', sellerId);

      // Gửi tất cả ảnh với key là "files" (khớp backend)
      images.forEach((img) => {
        formData.append('files', img);
      });

      const res = await axios.post('http://localhost:4003/api/product/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      alert('Sản phẩm đã được thêm thành công!');
      console.log(res.data);

      // Reset form
      setProductData({
        name: '',
        description: '',
        category: '',
        status: 'còn hàng',
        price: '',
        quantity: '',
      });
      setImages([]);

    } catch (error) {
      console.error('Lỗi khi thêm sản phẩm:', error.response?.data || error.message);
      alert('Thêm sản phẩm thất bại');
    }
  };

  return (
    <div>
      <h2>Thêm sản phẩm mới</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Tên sản phẩm" value={productData.name} onChange={handleChange} required />
        <input type="text" name="description" placeholder="Mô tả" value={productData.description} onChange={handleChange} required />
        <input type="text" name="category" placeholder="ID danh mục" value={productData.category} onChange={handleChange} required />
        <input type="text" name="price" placeholder="Giá" value={productData.price} onChange={handleChange} required />
        <input type="text" name="quantity" placeholder="Số lượng" value={productData.quantity} onChange={handleChange} required />

        <select name="status" value={productData.status} onChange={handleChange}>
          <option value="còn hàng">Còn hàng</option>
          <option value="hết hàng">Hết hàng</option>
        </select>

        <input type="file" name="images" multiple accept="image/*" onChange={handleImageChange} />

        <button type="submit">Thêm sản phẩm</button>
      </form>
    </div>
  );
};
