import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useParams, Link } from 'react-router-dom';
import { Button, Rate, Row, Col } from 'antd';
import { toSlug } from '../../utils/toSlug';
import { useAppContext } from '../../context/AppContext';

export const ProductDetails = () => {

  const { product, navigate } = useAppContext();
  const { id } = useParams();
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [thumbnail, setThumbnail] = useState(null);


  const currentProduct = product.find((item) => item.id === Number(id));

  useEffect(() => {
    if (product.length > 0) {
      let productsCopy = product.slice();
      productsCopy = productsCopy.filter((item) => currentProduct.category === item.category);
      setRelatedProducts(productsCopy.slice(0, 5));
    }
  }, [product, currentProduct]);

  useEffect(() => {
    setThumbnail(currentProduct?.img?.[0] || null);
  }, [currentProduct]);


  if (!currentProduct) return <p>Đang tải sản phẩm...</p>;

  return (
    <div className="product__details">
      <p className="product__details-nav">
        <Link to="/">Trang chủ</Link> /
        <Link to={"/product"}>Sản phẩm</Link> /
        <Link to={`/product/${toSlug(currentProduct.category)}`}>{currentProduct.category}</Link> /
        <span className="highlight">{currentProduct.name}</span>
      </p>

      <section className='product__details-container'>
        <Col span={10}>
          <div className="product__details-box">
            <h1>
              dasnbdasndanbdsamdnbAN
            </h1>
          </div>
        </Col>


        <Col span={14}>
          <div className='product__details-info'>
            <p>{currentProduct.name}</p>

          </div>

          <div className='product___details-review'>
            <p>{currentProduct.rating}</p>
            <div>
              <i class="fa-solid fa-star"></i>
              <i class="fa-solid fa-star"></i>
              <i class="fa-solid fa-star"></i>
              <i class="fa-solid fa-star"></i>
              <i class="fa-solid fa-star"></i>
            </div>
          </div>

          <div className=''>
            <span>An tâm mua sắm cùng Shopee</span>
          </div>

          <div className='product__details-price'>
            <p>đ{currentProduct.discounted_price}</p>
            <p>đ{currentProduct.original_price}</p>
            <p>{currentProduct.discount_percent}%</p>
          </div>

          <div className='product__details-count'>
            <span>Số lượng:</span>
            <span>{currentProduct.stock_quantity}</span>
          </div>

          <div className="product__details-addCart">
            <button className="" >
              Add to Cart
            </button>
            <button className="" >
              Buy now
            </button>
          </div>
        </Col>
      </section>

    </div>
  );
};
