import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './cart.css';

export const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')); // Lấy thông tin người dùng từ localStorage

  useEffect(() => {
    // Gọi API lấy giỏ hàng của người dùng
    if (user) {
      axios.get(`http://localhost:3003/api/cart/user/${user.id}`)
        .then((res) => {
          setCartItems(res.data);
          calculateTotalPrice(res.data); // Tính tổng giá trị giỏ hàng
        })
        .catch((err) => {
          console.error('Lỗi lấy giỏ hàng:', err);
        });
    } else {
      navigate('/login'); // Nếu không có người dùng, chuyển hướng đến trang đăng nhập
    }
  }, [user, navigate]);

  // Tính tổng giá trị giỏ hàng
  const calculateTotalPrice = (items) => {
    const total = items.reduce((acc, item) => {
      return acc + item.quantity * item.productId.price;
    }, 0);
    setTotalPrice(total);
  };

  // Hàm xử lý khi người dùng thay đổi số lượng sản phẩm
  const handleUpdateQuantity = (id, newQuantity) => {
    if (newQuantity <= 0) return;

    // Cập nhật số lượng trên API
    axios.put(`http://localhost:3003/api/cart/update/${id}`, { quantity: newQuantity })
      .then((res) => {
        const updatedItems = cartItems.map(item =>
          item._id === id ? { ...item, quantity: newQuantity } : item
        );
        setCartItems(updatedItems);
        calculateTotalPrice(updatedItems); // Tính lại tổng giá trị
      })
      .catch((err) => {
        console.error('Lỗi cập nhật số lượng:', err);
      });
  };

  // Hàm xử lý khi người dùng xóa sản phẩm
  const handleRemoveItem = (id) => {
    // Xóa sản phẩm khỏi giỏ hàng
    axios.delete(`http://localhost:3003/api/cart/delete/${id}`)
      .then((res) => {
        const updatedItems = cartItems.filter(item => item._id !== id);
        setCartItems(updatedItems);
        calculateTotalPrice(updatedItems); // Tính lại tổng giá trị
      })
      .catch((err) => {
        console.error('Lỗi xóa sản phẩm:', err);
      });
  };

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      {cartItems.length === 0 ? (
        <p>Giỏ hàng của bạn hiện đang trống</p>
      ) : (
        <>
          <div className="cart__items">
            {cartItems.map((item) => (
              <div key={item._id} className="cart__item">
                <img src={item.productId.image[0]} alt={item.productId.name} className="cart__item-img" />
                <div className="cart__item-info">
                  <h3>{item.productId.name}</h3>
                  <p>Giá: {item.productId.price} VND</p>
                  <p>Số lượng: 
                    <button onClick={() => handleUpdateQuantity(item._id, item.quantity - 1)}>-</button>
                    {item.quantity}
                    <button onClick={() => handleUpdateQuantity(item._id, item.quantity + 1)}>+</button>
                  </p>
                  <p>Thành tiền: {item.quantity * item.productId.price} VND</p>
                  <button onClick={() => handleRemoveItem(item._id)} className="remove-btn">Xóa</button>
                </div>
              </div>
            ))}
          </div>
          <div className="cart__total">
            <h3>Tổng cộng: {totalPrice} VND</h3>
            <button className="checkout-btn">Thanh toán</button>
          </div>
        </>
      )}
    </div>
  );
};
