      console.log("üë§ Th√¥ng tin ng∆∞·ªùi d√πng:", userRes.data);

      // Update the state with both orders and user data
      setOrders(orderRes.data || []);
    } catch (error) {
      console.error('L·ªói khi l·∫•y ƒë∆°n h√†ng ho·∫∑c th√¥ng tin ng∆∞·ªùi d√πng:', error);
    } finally {
      setLoading(false);
    }
  };

  if (user?.id) {
    fetchOrders();
  }
}, [user]);

  const handleCancelOrder = async (orderId) => {
    try {
      await axios.delete(`http://localhost:4000/api/order/cancel/${orderId}`);
      setOrders(orders.filter(order => order._id !== orderId));
      alert('ƒê∆°n h√†ng ƒë√£ ƒë∆∞·ª£c h·ªßy!');
