import React, { useState, useEffect } from 'react';
import './sellerform.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerForm = () => {
    const { user, seller } = useAppContext();
    const [images, setImages] = useState([]);
    const [storeName, setStoreName] = useState('');
    const [storeAddress, setStoreAddress] = useState('');
    const [phone, setPhone] = useState('');

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting form with data:', { storeName, storeAddress, phone, images });
        const formData = new FormData();

        formData.append('storeName', storeName);
        formData.append('storeAddress', storeAddress);
        formData.append('phone', phone);

        // Append images
        images.forEach((image) => {
            formData.append('image', image);
        });

        try {
            const url = seller ? `http://localhost:3006/api/seller/update/${seller._id}` : 'http://localhost:3006/api/seller/create';
            const method = seller ? 'put' : 'post';

            const response = await axios[method](url, formData, {
                headers: {
                    'Authorization': `Bearer ${user.token}`,
                    'Content-Type': 'multipart/form-data',
                },
            });

            if (response.status === 200 || response.status === 201) {
                alert('Seller saved successfully');
            }
        } catch (error) {
            console.error('Error saving seller:', error);
        }
    };

    return (
        <div className='form__seller-info'>
            <form className="form-container-seller" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Tên Shop</label>
                    <input
                        type="text"
                        className="form-input"
                        value={storeName}
                        onChange={(e) => setStoreName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Địa chỉ</label>
                    <input
                        type="text"
                        className="form-input"
                        value={storeAddress}
                        onChange={(e) => setStoreAddress(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group small">
                    <label className="form-label">Điện thoại</label>
                    <input
                        type="text"
                        className="form-input"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {[...Array(1)].map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="image"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>
                <button type="submit" className="submit-btn-save">Lưu</button>
                <button type="button" className="submit-btn-update">Cập nhật</button>
            </form>
        </div>
    );
};
