import React, { useState, useEffect } from 'react';
import './sellerform.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerForm = () => {
    const { user } = useAppContext();  // Lấy thông tin user từ context
    const [images, setImages] = useState([]);
    const [storeName, setStoreName] = useState('');
    const [storeAddress, setStoreAddress] = useState('');
    const [phone, setPhone] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);

    // Đọc dữ liệu từ localStorage khi component được load lại
    useEffect(() => {
        const savedStoreName = localStorage.getItem('storeName');
        const savedStoreAddress = localStorage.getItem('storeAddress');
        const savedPhone = localStorage.getItem('phone');

        if (savedStoreName) setStoreName(savedStoreName);
        if (savedStoreAddress) setStoreAddress(savedStoreAddress);
        if (savedPhone) setPhone(savedPhone);
    }, []);

    // Cập nhật giá trị trong localStorage khi người dùng thay đổi
    const handleInputChange = (e, setter) => {
        const { name, value } = e.target;
        setter(value);
        localStorage.setItem(name, value);  // Lưu giá trị vào localStorage
    };

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const userId = user?.id;  // Lấy ID người dùng từ context
        const token = localStorage.getItem('token');  // Lấy token từ localStorage

        if (!user || !userId || !token) {
            alert('Bạn chưa đăng nhập hoặc token bị thiếu.');
            return;
        }

        console.log('Form data:', storeName, storeAddress, phone, userId, images);

        const formData = new FormData();
        formData.append('storeName', storeName);
        formData.append('storeAddress', storeAddress);
        formData.append('phone', phone);
        formData.append('userId', userId);

        images.forEach((image) => {
            formData.append('image', image);
        });

        try {
            setIsSubmitting(true);
            const res = await axios.post('http://localhost:3006/api/seller/create', formData, {
                headers: {
                    'Authorization': `Bearer ${token}`,  // Thêm token vào header
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('Seller created:', res.data);
            alert('Tạo shop thành công!');
        } catch (error) {
            console.error('Error creating seller:', error.response?.data || error.message);
            alert('Có lỗi xảy ra khi tạo seller.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="form__seller-info">
            <form className="form-container-seller" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Tên Shop</label>
                    <input
                        type="text"
                        className="form-input"
                        name="storeName"
                        value={storeName}
                        onChange={(e) => handleInputChange(e, setStoreName)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Địa chỉ</label>
                    <input
                        type="text"
                        className="form-input"
                        name="storeAddress"
                        value={storeAddress}
                        onChange={(e) => handleInputChange(e, setStoreAddress)}
                        required
                    />
                </div>

                <div className="form-group small">
                    <label className="form-label">Điện thoại</label>
                    <input
                        type="text"
                        className="form-input"
                        name="phone"
                        value={phone}
                        onChange={(e) => handleInputChange(e, setPhone)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {[...Array(1)].map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>
                <button type="submit" className="submit-btn-save" disabled={isSubmitting}>
                    {isSubmitting ? 'Đang lưu...' : 'Lưu'}
                </button>
            </form>
        </div>
    );
};
