import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import { useAppContext } from '../../context/AppContext'; // Removed to fix error,  implement a mock if context is not available
import axios from 'axios';
// import './cart.css'; // Removed to fix error,  you can add inline styles or ensure the file exists.

// Mock useAppContext if the actual context is not available
const useAppContext = () => {
  const [user, setUser] = useState(null); // Or any initial user state you want
  return { user };
};

export const Cart = () => {
  const navigate = useNavigate();
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCart = async () => {
      if (!user) {
        navigate('/login');
        return;
      }
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        if (res.status >= 200 && res.status < 300) {
          setCartItems(res.data);
          setSelectedItems(res.data.map(item => item._id));
        } else {
          setError(`Failed to fetch cart: ${res.status}`);
        }

      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
        setError(error.message || 'An error occurred while fetching the cart.');
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [user, navigate]);

  const totalSelectedPrice = cartItems
    .filter(item => selectedItems.includes(item._id))
    .reduce((sum, item) => sum + item.productId.price * item.quantity, 0);

  const handleQuantityChange = async (cartId, action) => {
    const newQuantity = cartItems.find(item => item._id === cartId).quantity + (action === 'increase' ? 1 : -1);

    if (newQuantity <= 0) return;

    try {
      const res = await axios.put(`http://localhost:3003/api/cart/update/${cartId}`, { quantity: newQuantity });
      if (res.status >= 200 && res.status < 300) {
        setCartItems(cartItems.map(item => (item._id === cartId ? { ...item, quantity: newQuantity } : item)));
      } else {
        setError("Failed to update quantity");
      }
    } catch (error) {
      console.error('Lỗi khi thay đổi số lượng:', error);
      setError(error.message);
    }
  };

  const handleDeleteItem = async (cartId) => {
    try {
      const res = await axios.delete(`http://localhost:3003/api/cart/delete/${cartId}`);
      if (res.status >= 200 && res.status < 300) {
        setCartItems(cartItems.filter(item => item._id !== cartId));
      } else {
        setError("Failed to delete item");
      }

    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm khỏi giỏ:', error);
      setError(error.message);
    }
  };

  if (loading) return <p>Đang tải giỏ hàng...</p>;
  if (error) return <p>Error: {error}</p>

  return (
    <div className="cart" style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <h2 style={{ color: '#333' }}>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart" style={{ border: '1px solid #ddd', padding: '20px', borderRadius: '8px', marginBottom: '20px' }}>
          <p style={{ color: '#666' }}>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue" style={{ background: '#4CAF50', color: 'white', padding: '10px 15px', textDecoration: 'none', borderRadius: '5px' }}>Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header" style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={() => setSelectedItems(selectedItems.length === cartItems.length ? [] : cartItems.map(item => item._id))}
              style={{ marginRight: '10px' }}
            />
            <span style={{ fontWeight: 'bold' }}>Chọn tất cả</span>
          </div>

          <div className="cart-items" style={{ marginBottom: '20px' }}>
            {cartItems.map((item) => (
              <div className="cart-item" key={item._id} style={{ display: 'flex', borderBottom: '1px solid #ddd', paddingBottom: '15px', marginBottom: '15px', alignItems: 'center' }}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item._id)}
                  onChange={() => {
                    if (selectedItems.includes(item._id)) {
                      setSelectedItems(selectedItems.filter(id => id !== item._id));
                    } else {
                      setSelectedItems([...selectedItems, item._id]);
                    }
                  }}
                  style={{ marginRight: '10px' }}
                />
                <img src={item.productId.image[0]} alt={item.productId.name} style={{ width: '100px', height: '100px', objectFit: 'cover', marginRight: '10px' }} />
                <div className="cart-item-info" style={{ flex: 1 }}>
                  <h4 style={{ color: '#333', marginBottom: '5px' }}>{item.productId.name}</h4>
                  <p style={{ color: '#666', marginBottom: '5px' }}>Giá: đ{item.productId.price.toLocaleString()}</p>

                  <div className="quantity-control" style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
                    <button onClick={() => handleQuantityChange(item._id, 'decrease')} style={{ padding: '5px 10px', border: '1px solid #ddd', borderRadius: '4px', marginRight: '5px' }}>-</button>
                    <input type="text" value={item.quantity} readOnly style={{ width: '40px', textAlign: 'center', padding: '5px', border: '1px solid #ddd', borderRadius: '4px', marginRight: '5px' }} />
                    <button onClick={() => handleQuantityChange(item._id, 'increase')} style={{ padding: '5px 10px', border: '1px solid #ddd', borderRadius: '4px' }}>+</button>
                  </div>

                  <p style={{ color: '#666', marginBottom: '5px' }}>Thành tiền: đ{(item.productId.price * item.quantity).toLocaleString()}</p>

                  <button className="btn-delete" onClick={() => handleDeleteItem(item._id)} style={{ background: '#f44336', color: 'white', padding: '8px 12px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>Xóa</button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '20px' }}>
            <div className="footer-left" style={{ display: 'flex', alignItems: 'center' }}>
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={() => setSelectedItems(selectedItems.length === cartItems.length ? [] : cartItems.map(item => item._id))}
                style={{ marginRight: '10px' }}
              />
              <span style={{ fontWeight: 'bold' }}>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p style={{ fontWeight: 'bold', marginRight: '20px' }}>
                Tổng thanh toán: <span style={{ color: '#f44336' }}>đ{totalSelectedPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy"  style={{ background: '#f44336', color: 'white', padding: '10px 15px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Mua hàng</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
