import React, { useEffect, useState } from 'react'
import './productdetails.css'
import { useAppContext } from '../../context/AppContext'
import { useParams } from 'react-router-dom'
import { Button, Col, Row } from 'antd';
export const ProductDetails = () => {

  const { id } = useParams()
  const { product, navigate } = useAppContext()
  const [thumbnail, setThumnail] = useState(null)
  const [relatedProducts, setRelatedProducts] = useState(product.image[0])

  useEffect(() => {
    if (product.length > 0) {
      let productsCopy = product.slice();
      productsCopy = productsCopy.filter((item) => product.copy === item.category)
      setRelatedProducts(productsCopy.slice(0.5))
    }
  }, [product])

  useEffect(() => {
    setThumnail(product?.image[0] ? product.image[0] : null)
  }, [product])

  return (
    product && (
      <>
        <Row>

        </Row>
      </>
    )
  )
}
